INSERT INTO `TalkShowTexts` VALUES (314257, 7, 1, '不远。直走然后左转。', 'No. Go straight and turn left.', NULL, NULL, '39.2', '43.5');
INSERT INTO `TalkShowTexts` VALUES (314258, 1, 1, '医院在哪里？', 'Where is the hospital?', NULL, NULL, '0.7', '2.9');
INSERT INTO `TalkShowTexts` VALUES (314258, 4, 1, '电影院在哪里？', 'Where is the cinema?', NULL, NULL, '20.3', '23.1');
INSERT INTO `TalkShowTexts` VALUES (314258, 6, 1, '到书店来。', 'Come to the bookstore.', NULL, NULL, '25.6', '28.6');
INSERT INTO `TalkShowTexts` VALUES (314260, 2, 1, '嗨，孩子们。你来得好早。', 'Hi, children. You’re early. ', NULL, NULL, '6.3', '9.6');
INSERT INTO `TalkShowTexts` VALUES (314260, 4, 1, '我通常步行来。', 'Usually, I come on foot. ', NULL, NULL, '12.9', '15.7');
INSERT INTO `TalkShowTexts` VALUES (314260, 5, 1, '有时我坐公共汽车来。', 'Sometimes I come by bus.', NULL, NULL, '15.7', '18.2');
INSERT INTO `TalkShowTexts` VALUES (314260, 6, 1, '我经常骑自行车来。', 'I often come by bike.', NULL, NULL, '18.2', '20.4');
INSERT INTO `TalkShowTexts` VALUES (314260, 7, 1, '史密斯太太，你怎么来上学的？坐车？', 'How do you come to school, Mrs Smith? By car?', NULL, NULL, '21.0', '25.9');
INSERT INTO `TalkShowTexts` VALUES (314261, 6, 1, '一个头盔。在美国骑自行车的人必须戴头盔。', 'A helmet. In the USA people on bikes must wear one.', NULL, NULL, '30.8', '35.9');
INSERT INTO `TalkShowTexts` VALUES (314261, 10, 1, '我必须注意红绿灯！', 'I must pay attention to the traffic lights!', NULL, NULL, '51.3', '54.9');
INSERT INTO `TalkShowTexts` VALUES (314263, 2, 1, '你坐地铁去，', 'You go by subway,', NULL, NULL, '13.7', '16.6');
INSERT INTO `TalkShowTexts` VALUES (314263, 3, 1, '我坐出租车去。', 'And I’ll go by taxi.', NULL, NULL, '16.7', '19.0');
INSERT INTO `TalkShowTexts` VALUES (314263, 6, 1, '我要坐大船去。', 'And I’ll go by a big ship.', NULL, NULL, '26.6', '28.8');
INSERT INTO `TalkShowTexts` VALUES (314263, 7, 1, '我会比你先到苏格兰。', 'And I’ll be in Scotland before you.', NULL, NULL, '29.0', '34.2');
INSERT INTO `TalkShowTexts` VALUES (314265, 2, 1, '我要上美术课。', 'I’m going to have an art lesson.', NULL, NULL, '9.5', '13.0');
INSERT INTO `TalkShowTexts` VALUES (314266, 1, 1, '我的表弟杰克下周要来看我。', 'My cousin Jack is going to visit me next week.', NULL, NULL, '7.0', '10.6');
INSERT INTO `TalkShowTexts` VALUES (314266, 4, 1, '我们要去看一部关于太空旅行的电影!', 'We’re going to see a film about space travel!', NULL, NULL, '16.8', '20.3');
INSERT INTO `TalkShowTexts` VALUES (314266, 5, 1, '太酷了!我有很多关于太空的漫画书。', 'Cool! I have lots of comic books about space. ', NULL, NULL, '20.6', '25.1');
INSERT INTO `TalkShowTexts` VALUES (314266, 8, 1, '为什么不星期二去呢？那时就半价了！', 'Why not go on Tuesday? It’s half price then!', NULL, NULL, '30.9', '34.7');
INSERT INTO `TalkShowTexts` VALUES (314267, 1, 1, '你明天打算做什么?', 'What are you going to do tomorrow?', NULL, NULL, '7.3', '9.6');
INSERT INTO `TalkShowTexts` VALUES (314268, 4, 1, '当我长大了，我将在月球上行走。', 'When I grow up, I’ll walk on the moon.', NULL, NULL, '31.4', '34.0');
INSERT INTO `TalkShowTexts` VALUES (314270, 6, 1, '奥利弗，这是我的笔友彼得。 他来自新西兰。', 'Oliver, this is my pen pal Peter.  He’s from New Zealand.', NULL, NULL, '34.6', '40.5');
INSERT INTO `TalkShowTexts` VALUES (314271, 9, 1, '是的。我要教他中国歌曲《茉莉花》!', 'Yes. I’m going to teach him the Chinese song  \"Jasmine Flower\"!', NULL, NULL, '38.8', '44.6');
INSERT INTO `TalkShowTexts` VALUES (314271, 10, 1, '好主意！', 'Good idea!', NULL, NULL, '45.9', '47.4');
INSERT INTO `TalkShowTexts` VALUES (314272, 4, 1, '不，他没有。他住在堪培拉。', 'No, he doesn’t.  He lives in Canberra. ', NULL, NULL, '22.3', '26.7');
INSERT INTO `TalkShowTexts` VALUES (314272, 6, 1, '真的吗？他喜欢玩拼字游戏和徒步旅行吗？', 'Really? Does he like doing word puzzles and  going hiking?', NULL, NULL, '30.8', '36.7');
INSERT INTO `TalkShowTexts` VALUES (314272, 8, 1, '太神了！我也喜欢！', 'Amazing! I like those too!', NULL, NULL, '41.5', '45.3');
INSERT INTO `TalkShowTexts` VALUES (314277, 4, 1, '他是一个商人。', 'He’s a businessman. ', NULL, NULL, '14.1', '15.7');
INSERT INTO `TalkShowTexts` VALUES (314277, 7, 1, '她是校长。', 'She’s a head teacher.', NULL, NULL, '23.6', '25.5');
INSERT INTO `TalkShowTexts` VALUES (314278, 1, 1, '我叔叔是个渔夫。', 'My uncle is a fisherman.', NULL, NULL, '5.0', '7.6');
INSERT INTO `TalkShowTexts` VALUES (314278, 3, 1, '他在海上工作。', 'He works at sea. ', NULL, NULL, '10.6', '12.4');
INSERT INTO `TalkShowTexts` VALUES (314278, 5, 1, '我明白了。他怎么去上班?坐船吗?', 'I see. How does he go to work? By boat?', NULL, NULL, '16.6', '21.3');
INSERT INTO `TalkShowTexts` VALUES (314278, 9, 1, '是的。他工作很努力，身体很健康。', 'Yes. He works very hard and stays healthy.', NULL, NULL, '35.0', '39.5');
INSERT INTO `TalkShowTexts` VALUES (314280, 3, 1, '我的父亲是一个工人。', 'My father is a worker.', NULL, NULL, '23.4', '26.6');
INSERT INTO `TalkShowTexts` VALUES (314280, 8, 1, '我想成为一名飞行员。', 'I want to be a pilot.', NULL, NULL, '41.2', '44.4');
INSERT INTO `TalkShowTexts` VALUES (314280, 9, 1, '我想当邮递员。', 'I want to be a postman.', NULL, NULL, '44.8', '48.0');
INSERT INTO `TalkShowTexts` VALUES (314280, 10, 1, '我想成为一名科学家。', 'I want to be a scientist.', NULL, NULL, '48.4', '51.6');
INSERT INTO `TalkShowTexts` VALUES (314281, 14, 1, '妈妈，我病了。', 'Mum, I feel ill. ', NULL, NULL, '59.4', '63.0');
INSERT INTO `TalkShowTexts` VALUES (314282, 4, 1, '他追赶老鼠。他们怕他。', 'He chases the mice. They’re afraid of him.', NULL, NULL, '16.4', '20.9');
INSERT INTO `TalkShowTexts` VALUES (314282, 6, 1, '因为老鼠是坏的。他们伤害别人。', 'Because the mice are bad. They hurt people. ', NULL, NULL, '24.8', '29.4');
INSERT INTO `TalkShowTexts` VALUES (314283, 2, 1, '怎么了?', 'What’s wrong?', NULL, NULL, '8.2', '10.0');
INSERT INTO `TalkShowTexts` VALUES (314283, 4, 1, '他今天上午应该去看医生，所以我们今天不能去动物园了。', 'He should see a doctor this morning, so we can’t go to the zoo today.', NULL, NULL, '12.5', '18.0');
INSERT INTO `TalkShowTexts` VALUES (314283, 6, 1, '不要伤心。我们可以下次再去。', 'Don’t be sad. We can go next time.', NULL, NULL, '20.5', '24.4');
INSERT INTO `TalkShowTexts` VALUES (314283, 7, 1, '爸爸现在感觉怎么样?', 'How does Dad feel now?', NULL, NULL, '24.6', '26.8');
INSERT INTO `TalkShowTexts` VALUES (314283, 8, 1, '不太好。我们一起去医院吧。', 'Not well. Let’s go to the hospital.', NULL, NULL, '26.9', '30.4');
INSERT INTO `TalkShowTexts` VALUES (314284, 7, 1, '他在哪里？我现在有点担心。', 'Where is he? I’m a little worried now. ', NULL, NULL, '36.1', '39.5');
INSERT INTO `TalkShowTexts` VALUES (314284, 13, 1, '数到十，深呼吸。', 'Count to ten and take a deep breath. ', NULL, NULL, '53.5', '56.9');
INSERT INTO `TalkShowTexts` VALUES (314285, 4, 1, '如果你感到高兴并且你知道，那么你的脸一定会显露出来。', 'If you’re happy and you know it, then your face will surely show it.', NULL, NULL, '18.8', '23.3');
INSERT INTO `TalkShowTexts` VALUES (314285, 6, 1, '如果你感到生气并且你知道，你就跺跺脚。', 'If you’re angry and you know it, stamp your feet.', NULL, NULL, '34.5', '38.3');
INSERT INTO `TalkShowTexts` VALUES (314286, 1, 1, '我高21米。 我是最高的！', 'I’m 21 metres tall. I’m the tallest!', NULL, NULL, '4.1', '8.2');
INSERT INTO `TalkShowTexts` VALUES (314286, 3, 1, '我比这个恐龙高。', 'I’m taller than this dinosaur.', NULL, NULL, '12.2', '14.6');
INSERT INTO `TalkShowTexts` VALUES (314287, 1, 1, '看！ 那是这个大厅中最高的恐龙。', 'Look! That’s the tallest dinosaur in this hall.', NULL, NULL, '3.7', '7.6');
INSERT INTO `TalkShowTexts` VALUES (314287, 4, 1, '哇！ 比我们俩都高。', 'Wow! It’s taller than both of us together.', NULL, NULL, '17.8', '22.0');
INSERT INTO `TalkShowTexts` VALUES (314288, 2, 1, '我们来看一下。', 'Let’s have a look.', NULL, NULL, '9.0', '10.7');
INSERT INTO `TalkShowTexts` VALUES (314288, 10, 1, '但是我认为你比我重。', 'But I think you are heavier.', NULL, NULL, '38.9', '40.8');
INSERT INTO `TalkShowTexts` VALUES (314288, 12, 1, '我48公斤。', 'I’m 48 kilograms.', NULL, NULL, '43.4', '45.7');
INSERT INTO `TalkShowTexts` VALUES (314290, 4, 1, '它比你的猫更小，更矮，更年轻。', 'He’s smaller and shorter and younger than yours.', NULL, NULL, '27.4', '31.4');
INSERT INTO `TalkShowTexts` VALUES (314290, 7, 1, '我的大狗比你的狗大。', 'My big dog is bigger than yours.', NULL, NULL, '50.8', '57.4');
INSERT INTO `TalkShowTexts` VALUES (314290, 8, 1, '它比你的狗更大，更长，更强壮。', 'He’s bigger and longer and stronger than yours.', NULL, NULL, '58.0', '62.0');
INSERT INTO `TalkShowTexts` VALUES (314291, 1, 1, '你上周末做了什么？', 'What did you do last weekend?', NULL, NULL, '4.4', '6.8');
INSERT INTO `TalkShowTexts` VALUES (314291, 2, 1, '星期六我打扫房间，洗衣服。', 'I cleaned my room and washed my clothes on Saturday.', NULL, NULL, '8.0', '11.9');
INSERT INTO `TalkShowTexts` VALUES (314292, 6, 1, '好吧，我和你奶奶呆在家里。', 'Well, I stayed at home with your grandma.', NULL, NULL, '22.3', '25.2');
INSERT INTO `TalkShowTexts` VALUES (314292, 7, 1, '我们下午喝茶，看电视。', 'We drank tea in the afternoon and watched TV.', NULL, NULL, '25.4', '29.1');
INSERT INTO `TalkShowTexts` VALUES (314292, 9, 1, '我在电视上看了一些儿童节目。', 'I watched some children’s shows on TV.', NULL, NULL, '31.4', '34.1');
INSERT INTO `TalkShowTexts` VALUES (314293, 2, 1, '我想买本新的电影杂志。', 'I want to buy the new film magazine.', NULL, NULL, '7.5', '10.4');
INSERT INTO `TalkShowTexts` VALUES (314293, 3, 1, '哦，我上周末读过。', 'Oh, I read it last weekend.', NULL, NULL, '11.3', '14.0');
INSERT INTO `TalkShowTexts` VALUES (314293, 8, 1, '没有，我感冒了。 我整个周末都呆在家里睡觉。', 'No, I had a cold. I stayed at home all weekend and slept.', NULL, NULL, '27.9', '32.5');
INSERT INTO `TalkShowTexts` VALUES (314293, 9, 1, '哦，很抱歉听到这个消息。 我很高兴你现在感觉好一些。', 'Oh, I’m sorry. I’m happy you feel better now.', NULL, NULL, '33.1', '37.0');
INSERT INTO `TalkShowTexts` VALUES (314293, 10, 1, '谢谢。 那我们坐公共汽车去。这比走路快。', 'Thanks. Let’s go by bus. It’s faster than walking.', NULL, NULL, '38.2', '42.5');
INSERT INTO `TalkShowTexts` VALUES (314294, 8, 1, '然后我去钓鱼。', 'Then I went fishing.', NULL, NULL, '28.6', '30.0');
INSERT INTO `TalkShowTexts` VALUES (314295, 4, 1, '我看了电影，打了乒乓球。 你上周末做了什么？', 'I saw a film and played ping-pong. What did you do last weekend?', NULL, NULL, '19.8', '25.6');
INSERT INTO `TalkShowTexts` VALUES (314296, 2, 1, '我从自行车上摔下来，伤了脚。', 'I fell off my bike and hurt my foot.', NULL, NULL, '11.0', '14.1');
INSERT INTO `TalkShowTexts` VALUES (314296, 6, 1, '当然！ 你可以在劳动节假期中看到我的照片。', 'Sure! You can see my photos from the Labour Day holiday.', NULL, NULL, '28.0', '31.8');
INSERT INTO `TalkShowTexts` VALUES (314296, 7, 1, '你去吐鲁番了吗？', 'Did you go to Turpan?', NULL, NULL, '33.5', '35.3');
INSERT INTO `TalkShowTexts` VALUES (314296, 9, 1, '我看到很多葡萄，也吃了很多羊肉串。', 'I saw lots of grapes and ate lots of mutton kebabs.', NULL, NULL, '37.2', '40.9');
INSERT INTO `TalkShowTexts` VALUES (314297, 8, 1, '我骑马。 看，它很小！', 'I rode a horse. Look, it’s very small!', NULL, NULL, '25.8', '28.8');
INSERT INTO `TalkShowTexts` VALUES (314297, 9, 1, '哦，是的，看起来像骡子！', 'Oh, yes. It looks like a mule!', NULL, NULL, '31.9', '34.5');
INSERT INTO `TalkShowTexts` VALUES (314297, 12, 1, '我们在那儿看到很多葡萄，但我们不能吃。', 'We saw lots of grapes there, but we couldn’t eat them.', NULL, NULL, '39.6', '43.3');
INSERT INTO `TalkShowTexts` VALUES (314297, 13, 1, '他们要到八月才熟。', 'They won’t be ready till August.', NULL, NULL, '43.6', '45.7');
INSERT INTO `TalkShowTexts` VALUES (314298, 2, 1, '我和我的家人去了三亚。', 'My family and I went to Sanya.', NULL, NULL, '9.6', '11.9');
INSERT INTO `TalkShowTexts` VALUES (314298, 8, 1, '海滩怎么样？', 'How was the beach?', NULL, NULL, '28.8', '30.4');
INSERT INTO `TalkShowTexts` VALUES (314298, 11, 1, '我拍了很多照片，也去游泳了。', 'I took lots of pictures, and I also went swimming.', NULL, NULL, '35.1', '39.1');
INSERT INTO `TalkShowTexts` VALUES (314300, 7, 1, '我买了礼物给你。', 'I bought a gift for you.', NULL, NULL, '39.9', '43.1');
INSERT INTO `TalkShowTexts` VALUES (314301, 8, 1, '也没有健身房！', 'There was no gym either!', NULL, NULL, '43.9', '45.9');
INSERT INTO `TalkShowTexts` VALUES (314302, 4, 1, '晚上可以看到星星吗？', 'Could you see stars at night?', NULL, NULL, '20.3', '22.6');
INSERT INTO `TalkShowTexts` VALUES (314302, 9, 1, '这简单，我在互联网上查询了一下。', 'Easy, I looked it up on the Internet.', NULL, NULL, '40.2', '43.6');
INSERT INTO `TalkShowTexts` VALUES (314303, 5, 1, '现在我们都不同了！', 'We are all different now!', NULL, NULL, '27.3', '29.3');
INSERT INTO `TalkShowTexts` VALUES (314303, 7, 1, '现在，我在课堂上很活跃。', 'Now, I’m very active in class.', NULL, NULL, '34.0', '36.2');
INSERT INTO `TalkShowTexts` VALUES (314303, 10, 1, '现在，我每天骑自行车。 我非常喜欢骑自行车。', 'Now, I go cycling every day. I like it very much.', NULL, NULL, '43.0', '47.6');
INSERT INTO `TalkShowTexts` VALUES (315006, 1, 1, '嗨！ 早上好，猫！', 'Hi! Good morning, Cat!', NULL, NULL, '0.1', '3.4');
INSERT INTO `TalkShowTexts` VALUES (315006, 4, 1, '看！ 一个鼠标！', 'Look! A mouse!', NULL, NULL, '17.4', '19.1');
INSERT INTO `TalkShowTexts` VALUES (315006, 8, 1, '我是猴子！ 再见！', 'I’m Monkey! Goodbye!', NULL, NULL, '32.1', '35.4');
INSERT INTO `TalkShowTexts` VALUES (315010, 1, 1, '嗨，凯特！ 这是我的橡皮擦。', 'Hi, Kate! This is my eraser.', NULL, NULL, '4.6', '7.7');
INSERT INTO `TalkShowTexts` VALUES (315010, 2, 1, '嗨，彼得 这是我的卷笔刀。', 'Hi, Peter. This is my sharpener.', NULL, NULL, '8.0', '11.5');
INSERT INTO `TalkShowTexts` VALUES (315011, 1, 1, '嗨李妍 这是我的胶水。', 'Hi, Li Yan! This is my glue.', NULL, NULL, '4.5', '7.9');
INSERT INTO `TalkShowTexts` VALUES (315011, 2, 1, '看，凯特！ 这是我的记号笔。', 'Look, Kate! This is my marker.', NULL, NULL, '8.0', '10.8');
INSERT INTO `TalkShowTexts` VALUES (315012, 3, 1, '看我的书。', 'Look at my book.', NULL, NULL, '13.1', '15.6');
INSERT INTO `TalkShowTexts` VALUES (315012, 7, 1, '这是我的笔，铅笔，尺子...', 'This is my pen, my pencil,  my ruler...', NULL, NULL, '30.7', '35.4');
INSERT INTO `TalkShowTexts` VALUES (315013, 3, 1, '嗨！ 看我的眼睛。', 'Hi! Look at my eye.', NULL, NULL, '13.5', '17.0');
INSERT INTO `TalkShowTexts` VALUES (315014, 3, 1, '看我的脸！', 'Look at my face!', NULL, NULL, '7.1', '9.5');
INSERT INTO `TalkShowTexts` VALUES (315016, 3, 1, '看我的胳膊！', 'Look at my arm!', NULL, NULL, '5.3', '6.8');
INSERT INTO `TalkShowTexts` VALUES (315017, 2, 1, '不太好。 看我的腿！', 'Not so well. Look at my leg!', NULL, NULL, '2.3', '6.1');
INSERT INTO `TalkShowTexts` VALUES (315018, 1, 1, '看耳朵！', 'Look at the ears!', NULL, NULL, '1.5', '3.9');
INSERT INTO `TalkShowTexts` VALUES (315018, 3, 1, '看鼻子！', 'Look at the nose!', NULL, NULL, '12.2', '14.5');
INSERT INTO `TalkShowTexts` VALUES (315018, 5, 1, '看手！', 'Look at the hand!', NULL, NULL, '20.8', '23.5');
INSERT INTO `TalkShowTexts` VALUES (315018, 7, 1, '看脚！', 'Look at the foot!', NULL, NULL, '26.6', '29.0');
INSERT INTO `TalkShowTexts` VALUES (315020, 1, 1, '嗨，彼得 我有一个气球。', 'Hi, Peter. I have a balloon.', NULL, NULL, '3.4', '6.7');
INSERT INTO `TalkShowTexts` VALUES (315024, 2, 1, '凉！ 我有一个洋娃娃。', 'Cool! I have a doll.', NULL, NULL, '5.5', '8.1');
INSERT INTO `TalkShowTexts` VALUES (315024, 6, 1, '我有风筝，球，汽车，飞机...', 'I have a kite, a ball, a car, a plane...', NULL, NULL, '22.1', '28.9');
INSERT INTO `TalkShowTexts` VALUES (315027, 4, 1, '绿色，紫色和棕色。', 'Green, purple and brown.', NULL, NULL, '9.7', '12.7');
INSERT INTO `TalkShowTexts` VALUES (315029, 3, 1, '将其上色为粉红色，橙色和灰色。', 'Colour it pink, orange and grey.', NULL, NULL, '4.6', '8.1');
INSERT INTO `TalkShowTexts` VALUES (315030, 2, 1, '蓝色！', 'Blue!', NULL, NULL, '15.0', '16.1');
INSERT INTO `TalkShowTexts` VALUES (315030, 4, 1, '没有！ 是黄色的。', 'No! It’s yellow.', NULL, NULL, '19.8', '22.2');
INSERT INTO `TalkShowTexts` VALUES (315030, 6, 1, '红色和橙色。', 'Red and orange.', NULL, NULL, '34.3', '36.4');
INSERT INTO `TalkShowTexts` VALUES (315030, 7, 1, '黑与白。', 'Black and white.', NULL, NULL, '37.7', '39.9');
INSERT INTO `TalkShowTexts` VALUES (315030, 8, 1, '没有！ 它是绿色和紫色。', 'No! It’s green and purple.', NULL, NULL, '40.7', '43.0');
INSERT INTO `TalkShowTexts` VALUES (315030, 12, 1, '它是粉红色的。', 'It’s pink.', NULL, NULL, '70.6', '72.2');
INSERT INTO `TalkShowTexts` VALUES (315032, 1, 1, '嗨，凯特！ 这是给你的蛋糕！', 'Hi, Kate! Here’s a cake for you!', NULL, NULL, '0.7', '3.4');
INSERT INTO `TalkShowTexts` VALUES (315033, 1, 1, '来个苹果。', 'Have an apple.', NULL, NULL, '0.4', '1.8');
INSERT INTO `TalkShowTexts` VALUES (315033, 2, 1, '不用了，谢谢。 我喜欢梨。', 'No, thanks. I like pears.', NULL, NULL, '2.2', '4.8');
INSERT INTO `TalkShowTexts` VALUES (315034, 1, 1, '妈妈，能给我一些果汁吗？', 'Mum, can I have some juice, please?', NULL, NULL, '0.1', '3.4');
INSERT INTO `TalkShowTexts` VALUES (315036, 1, 1, '嗨，我是大鲍勃。 我喜欢汉堡包，热狗，可乐...', 'Hi, I’m Big Bob. I like hamburgers, hot dogs, Coke ...', NULL, NULL, '0.8', '9.8');
INSERT INTO `TalkShowTexts` VALUES (315036, 2, 1, '鲍勃，这是给你的鸡肉。', 'Bob, here’s some chicken for you.', NULL, NULL, '13.8', '16.7');
INSERT INTO `TalkShowTexts` VALUES (315036, 4, 1, '我可以喝点水吗？', 'Can I have some water, please?', NULL, NULL, '21.4', '24.8');
INSERT INTO `TalkShowTexts` VALUES (315037, 3, 1, '我们一起去上学吧。', 'Let’s go to school.', NULL, NULL, '25.5', '26.9');
INSERT INTO `TalkShowTexts` VALUES (315038, 6, 1, '去上学。去教室。去上学。', 'Go to school. Go to the classroom. Go to school.', NULL, NULL, '23.8', '28.7');
INSERT INTO `TalkShowTexts` VALUES (315039, 1, 1, '你好，杨明。我有一本新课本。', 'Hello, Yang Ming. I have a new textbook.', NULL, NULL, '10.8', '13.8');
INSERT INTO `TalkShowTexts` VALUES (315039, 2, 1, '看！我有一本新故事书。', 'Look! I have a new storybook.', NULL, NULL, '17.4', '20.0');
INSERT INTO `TalkShowTexts` VALUES (315041, 4, 1, '对。看！一本汉语书。', 'Yes. Look! A Chinese book.', NULL, NULL, '21.1', '23.8');
INSERT INTO `TalkShowTexts` VALUES (315041, 5, 1, '还有一本英语书。', 'And an English book.', NULL, NULL, '26.2', '28.0');
INSERT INTO `TalkShowTexts` VALUES (315042, 2, 1, '笔记本？', 'A notebook?', NULL, NULL, '5.5', '6.8');
