INSERT INTO `TalkShowTexts` VALUES (315297, 7, 1, '四月是一年中的第四个月。', 'April is the fourth month of the year.', NULL, NULL, '33.8', '37.1');
INSERT INTO `TalkShowTexts` VALUES (315297, 8, 1, '复活节在三月或四月。', 'Easter is in March or April.', NULL, NULL, '37.8', '41.2');
INSERT INTO `TalkShowTexts` VALUES (315297, 11, 1, '在复活节期间，孩子们能得到五颜六色的复活节彩蛋。', 'During Easter, children get colourful Easter eggs.', NULL, NULL, '52.6', '57.7');
INSERT INTO `TalkShowTexts` VALUES (315299, 1, 1, '五月是一年中的第五个月。', 'May is the fifth month of the year. ', NULL, NULL, '0.1', '3.5');
INSERT INTO `TalkShowTexts` VALUES (315299, 2, 1, '5月1日是劳动节。', 'Labour Day, or May Day,  is on May 1st. ', NULL, NULL, '3.6', '8.2');
INSERT INTO `TalkShowTexts` VALUES (315299, 4, 1, '母亲节也在五月。', 'Mother’s Day is also in May. ', NULL, NULL, '12.8', '15.7');
INSERT INTO `TalkShowTexts` VALUES (315299, 6, 1, '六月是一年中的第六个月。', 'June is the sixth month of the year. ', NULL, NULL, '21.0', '24.2');
INSERT INTO `TalkShowTexts` VALUES (315299, 7, 1, '六月的第一天是中国的儿童节。', 'The first day of June is Children’s Day  in China.', NULL, NULL, '24.3', '28.8');
INSERT INTO `TalkShowTexts` VALUES (315299, 9, 1, '父亲节也在六月。', 'Father’s Day is in June, too. ', NULL, NULL, '32.6', '35.5');
INSERT INTO `TalkShowTexts` VALUES (315301, 1, 1, '七月是一年中的第七个月。', 'July is the seventh month of the year.', NULL, NULL, '0.6', '4.0');
INSERT INTO `TalkShowTexts` VALUES (315301, 2, 1, '7月1日是中国共产党（CPC）的生日。', 'July 1st is the birthday of the Communist Party  of China (CPC).', NULL, NULL, '5.2', '10.2');
INSERT INTO `TalkShowTexts` VALUES (315301, 4, 1, '八月是一年中的第八个月。', 'August is the eighth month of the year.', NULL, NULL, '16.2', '19.5');
INSERT INTO `TalkShowTexts` VALUES (315301, 5, 1, '人民解放军（PLA）日是8月1日。', 'The People’s Liberation Army (PLA) Day  is on August 1st.', NULL, NULL, '21.0', '26.1');
INSERT INTO `TalkShowTexts` VALUES (315303, 1, 1, '九月是一年中的第九个月。', 'September is the ninth month of the year. ', NULL, NULL, '0.6', '4.0');
INSERT INTO `TalkShowTexts` VALUES (315303, 3, 1, '他们在9月10日庆祝教师节。', 'They celebrate Teachers’ Day on  September 10th.', NULL, NULL, '6.9', '11.2');
INSERT INTO `TalkShowTexts` VALUES (315303, 5, 1, '十月是一年中的第十个月。', 'October is the tenth month of the year.', NULL, NULL, '18.1', '22.1');
INSERT INTO `TalkShowTexts` VALUES (315303, 6, 1, '中国的国庆节是10月1日。', 'China’s National Day is on October 1st.', NULL, NULL, '22.6', '26.6');
INSERT INTO `TalkShowTexts` VALUES (315306, 1, 1, '11月是一年中的第十一个月。', 'November is the eleventh month of the year.', NULL, NULL, '0.0', '4.2');
INSERT INTO `TalkShowTexts` VALUES (315306, 2, 1, '在美国，感恩节是11月的第4个星期四。', 'In America, Thanksgiving Day is on the 4th  Thursday of November.', NULL, NULL, '4.3', '10.7');
INSERT INTO `TalkShowTexts` VALUES (315306, 4, 1, '十二月是一年中的第十二个月，也是最后一个月。', 'December is the twelfth and last month  of the year.', NULL, NULL, '17.1', '22.0');
INSERT INTO `TalkShowTexts` VALUES (315306, 5, 1, '圣诞节是12月25日。', 'Christmas is on December 25th. ', NULL, NULL, '22.1', '25.5');
INSERT INTO `TalkShowTexts` VALUES (315310, 1, 1, '一年有四个季节。', 'There are four seasons in a year.', NULL, NULL, '0.2', '3.3');
INSERT INTO `TalkShowTexts` VALUES (315310, 3, 1, '在中国，春天大约在三月开始。', 'In China, springtime begins around March. ', NULL, NULL, '7.0', '11.1');
INSERT INTO `TalkShowTexts` VALUES (315310, 6, 1, '春天是绿色的季节。', 'Spring is the green season. ', NULL, NULL, '22.1', '24.9');
INSERT INTO `TalkShowTexts` VALUES (315311, 5, 1, '农民们正忙着。他们耕种土地。', 'Farmers are busy. They farm the land.', NULL, NULL, '18.8', '22.6');
INSERT INTO `TalkShowTexts` VALUES (315311, 6, 1, '人们出去郊游。', 'People go on outings. ', NULL, NULL, '22.9', '25.0');
INSERT INTO `TalkShowTexts` VALUES (315311, 7, 1, '孩子们放风筝。', 'Children fly kites. ', NULL, NULL, '25.4', '27.5');
INSERT INTO `TalkShowTexts` VALUES (315312, 1, 1, '两只春天的小鸟，在天空中歌唱。', 'Two little spring birds, Singing in the sky.', NULL, NULL, '6.5', '9.5');
INSERT INTO `TalkShowTexts` VALUES (315314, 1, 1, '夏天是一年中第二个也是最热的季节。', 'Summer is the second and the hottest  season of the year.', NULL, NULL, '0.4', '5.0');
INSERT INTO `TalkShowTexts` VALUES (315314, 3, 1, '天气很热，会经常下雨。', 'The weather is very hot and there’s  often a lot of rain. ', NULL, NULL, '9.8', '14.9');
INSERT INTO `TalkShowTexts` VALUES (315314, 4, 1, '有时会下大雨。', 'Sometimes there is heavy rain.', NULL, NULL, '15.7', '18.2');
INSERT INTO `TalkShowTexts` VALUES (315314, 5, 1, '夏天，学校放假，学生放假。', 'In summer, schools close and students  have their holidays.', NULL, NULL, '18.7', '23.8');
INSERT INTO `TalkShowTexts` VALUES (315314, 6, 1, '他们中的一些人与他们的父母一起去旅行，', 'Some of them go on trips with their parents, ', NULL, NULL, '24.3', '27.4');
INSERT INTO `TalkShowTexts` VALUES (315314, 7, 1, '一些和他们的老师去露营，', 'some go camping with their teachers,', NULL, NULL, '27.4', '30.1');
INSERT INTO `TalkShowTexts` VALUES (315314, 9, 1, '他们都喜欢暑假。', 'They all enjoy the summer holidays.', NULL, NULL, '33.0', '36.0');
INSERT INTO `TalkShowTexts` VALUES (315318, 7, 1, '这是10月。秋天来了。', 'It is October. Autumn is here. ', NULL, NULL, '38.5', '42.4');
INSERT INTO `TalkShowTexts` VALUES (315318, 8, 1, '树叶有红的、黄的和棕色的。', 'The leaves are red, yellow and brown.', NULL, NULL, '42.5', '46.6');
INSERT INTO `TalkShowTexts` VALUES (315319, 3, 1, '现在正在下雪。现在是冬天。', 'It is snowing. It is winter time. ', NULL, NULL, '8.6', '12.3');
INSERT INTO `TalkShowTexts` VALUES (315320, 9, 1, '我们白天喜欢日光浴，晚上喜欢月亮和星星。', 'We enjoyed sunbathing during the day, and we enjoyed the moon and stars at night.', NULL, NULL, '32.5', '39.5');
INSERT INTO `TalkShowTexts` VALUES (315320, 12, 1, '我呆在家里，和家人一起享受春节。', 'I stayed at home and enjoyed Spring Festival with my family.', NULL, NULL, '48.0', '51.7');
INSERT INTO `TalkShowTexts` VALUES (315320, 13, 1, '我还与杨明，彼得，李艳和丽莎举行了大型聚会。', 'I also had a big party with Yang Ming, Peter, Li Yan and Lisa.', NULL, NULL, '51.9', '57.0');
INSERT INTO `TalkShowTexts` VALUES (315322, 3, 1, '我们三年前开始学习英语。', 'We began to learn English three years ago.', NULL, NULL, '10.3', '13.2');
INSERT INTO `TalkShowTexts` VALUES (315322, 4, 1, '我们已经学到很多单词和句子，并且可以在课堂内外使用它们。', 'We have learned a lot of words and sentences,  and we can use them in and out of class.', NULL, NULL, '14.8', '21.3');
INSERT INTO `TalkShowTexts` VALUES (315322, 7, 1, '现在我们可以玩游戏并用英语做任务。', 'Now we can play games and do tasks in English.', NULL, NULL, '40.3', '43.6');
INSERT INTO `TalkShowTexts` VALUES (315323, 12, 1, '在小组中，我们总是互相帮助，互相学习。', 'In groups we always helped each other and learned from each other.', NULL, NULL, '39.6', '43.8');
INSERT INTO `TalkShowTexts` VALUES (315324, 1, 1, '什么时候使用？ 我们一次又一次地使用。', 'When do you use when? We use when again and again.', NULL, NULL, '11.0', '15.2');
INSERT INTO `TalkShowTexts` VALUES (315325, 11, 1, '我们玩了电脑游戏。', 'We played computer games.', NULL, NULL, '49.0', '51.4');
INSERT INTO `TalkShowTexts` VALUES (315325, 14, 1, '米奇跳起来。 你知道为什么吗？', 'Micky jumps up. Do you know why?', NULL, NULL, '69.3', '72.6');
INSERT INTO `TalkShowTexts` VALUES (315326, 5, 1, '我家附近有一个公园，公园后面有一条河。', 'There is a park near my home and there is a river behind the park.', NULL, NULL, '12.2', '17.3');
INSERT INTO `TalkShowTexts` VALUES (315326, 9, 1, '银行旁边是医院。', 'Beside the bank is a hospital.', NULL, NULL, '26.4', '29.1');
INSERT INTO `TalkShowTexts` VALUES (315326, 10, 1, '马路对面，酒店和书店之间有一家大型超市。', 'Across the road, there is a big supermarket between a hotel and a bookshop.', NULL, NULL, '29.4', '35.7');
INSERT INTO `TalkShowTexts` VALUES (315326, 11, 1, '我妈妈和我经常在那里买水果，蔬菜和其他健康食品。', 'My mum and I often buy fruit, vegetables and other healthy food there.', NULL, NULL, '36.6', '43.2');
INSERT INTO `TalkShowTexts` VALUES (315328, 8, 1, '沿着这条街走，在红绿灯处右转。', 'Go down this street and turn right at the traffic lights.', NULL, NULL, '34.2', '39.1');
INSERT INTO `TalkShowTexts` VALUES (315328, 10, 1, '步行到那里需要多长时间？', 'How long does it take to go there on foot?', NULL, NULL, '44.8', '47.7');
INSERT INTO `TalkShowTexts` VALUES (315328, 11, 1, '只有几分钟。', 'Only a few minutes.', NULL, NULL, '48.4', '50.6');
INSERT INTO `TalkShowTexts` VALUES (315330, 2, 1, '我如何去市图书馆？', 'How can I get to the City Library?', NULL, NULL, '7.7', '10.3');
INSERT INTO `TalkShowTexts` VALUES (315330, 5, 1, '不，乘公共汽车大约半小时。', 'No, it’s about half an hour by bus.', NULL, NULL, '19.8', '23.2');
INSERT INTO `TalkShowTexts` VALUES (315330, 8, 1, '您可以在第三站下车，然后在右边找到一家超市。', 'You can get off the bus at the third stop, and then you’ll find a supermarket on your right.', NULL, NULL, '32.4', '38.8');
INSERT INTO `TalkShowTexts` VALUES (315332, 2, 1, '书店在哪里？', 'Where is the bookshop?', NULL, NULL, '8.8', '10.9');
INSERT INTO `TalkShowTexts` VALUES (315332, 6, 1, '你会看到一家超市。', 'You’ll see a supermarket.', NULL, NULL, '21.2', '23.0');
INSERT INTO `TalkShowTexts` VALUES (315332, 9, 1, '走到酒店后面，然后右转。', 'Walk behind the hotel, and then turn right.', NULL, NULL, '32.0', '35.7');
INSERT INTO `TalkShowTexts` VALUES (315333, 3, 1, '这是我一家人的旅行计划。', 'This is my family’s travel plan. ', NULL, NULL, '5.1', '7.9');
INSERT INTO `TalkShowTexts` VALUES (315333, 6, 1, '在第一周，我们将去北京，哈尔滨和西安。', 'In the first week, we are going to visit Beijing, Harbin and Xi’an.', NULL, NULL, '18.2', '24.8');
INSERT INTO `TalkShowTexts` VALUES (315333, 7, 1, '我知道长城在北京。', 'I know the Great Wall is in Beijing. ', NULL, NULL, '25.3', '28.2');
INSERT INTO `TalkShowTexts` VALUES (315333, 9, 1, '第二周，我们将参观上海，杭州和三亚。', 'The second week, we are going to visit Shanghai, Hangzhou and Sanya. ', NULL, NULL, '32.5', '39.2');
INSERT INTO `TalkShowTexts` VALUES (315333, 11, 1, '我听说这是一个非常美丽的湖。', 'I hear it is a very beautiful lake. ', NULL, NULL, '43.6', '46.8');
INSERT INTO `TalkShowTexts` VALUES (315333, 18, 1, '它以兵马俑而闻名。', 'It is famous for the terracotta army.', NULL, NULL, '77.3', '80.3');
INSERT INTO `TalkShowTexts` VALUES (315335, 2, 1, '他们将在北京停留三天。', 'They are going to stay in Beijing for three days.', NULL, NULL, '5.3', '9.2');
INSERT INTO `TalkShowTexts` VALUES (315335, 3, 1, '导游告诉他们要去哪里。', 'The tour guide is telling them where they are going to visit.', NULL, NULL, '10.1', '13.9');
INSERT INTO `TalkShowTexts` VALUES (315335, 5, 1, '今天早上我们将参观长城，下午将参观明十三陵。', 'We’re going to visit the Great Wall this morning and then the Ming Tombs in the afternoon.', NULL, NULL, '19.8', '24.8');
INSERT INTO `TalkShowTexts` VALUES (315335, 6, 1, '长城离这里很远吗？', 'Is the Great Wall far from here?', NULL, NULL, '26.2', '28.7');
INSERT INTO `TalkShowTexts` VALUES (315335, 10, 1, '我们什么时候去颐和园？', 'When are we going to the Summer Palace?', NULL, NULL, '39.6', '42.6');
INSERT INTO `TalkShowTexts` VALUES (315335, 11, 1, '明天早上。', 'Tomorrow morning.', NULL, NULL, '43.5', '44.8');
INSERT INTO `TalkShowTexts` VALUES (315335, 12, 1, '然后，我们将参观紫禁城和天坛。', 'Then we’re going to visit the Forbidden City and the Temple of Heaven.', NULL, NULL, '45.1', '49.4');
INSERT INTO `TalkShowTexts` VALUES (315336, 4, 1, '她洗了玩具船。', 'She washed her toy ships.', NULL, NULL, '14.4', '16.5');
INSERT INTO `TalkShowTexts` VALUES (315337, 2, 1, '你能看到大本钟和那边的塔桥吗？', 'Can you see Big Ben and the Tower Bridge over there?', NULL, NULL, '9.6', '13.6');
INSERT INTO `TalkShowTexts` VALUES (315337, 7, 1, '美国的迪士尼乐园是世界上第一个也是最著名的迪士尼乐园。', 'Disneyland in America is the first and the most famous one in the world.', NULL, NULL, '28.1', '33.4');
INSERT INTO `TalkShowTexts` VALUES (315337, 10, 1, '好。 然后，我们将参观加拿大的加拿大国家电视塔。', 'OK. Then we’ll visit the CN Tower in Canada.', NULL, NULL, '40.1', '44.4');
INSERT INTO `TalkShowTexts` VALUES (315337, 13, 1, '那是尼亚加拉大瀑布。', 'That’s Niagara Falls.', NULL, NULL, '54.1', '56.0');
INSERT INTO `TalkShowTexts` VALUES (315337, 16, 1, '您会喜欢悉尼歌剧院。', 'You’ll like the Sydney Opera House.', NULL, NULL, '62.8', '66.0');
INSERT INTO `TalkShowTexts` VALUES (315337, 18, 1, '我想看看所有那些著名的地方。', 'I want to see all those famous places.', NULL, NULL, '68.9', '72.1');
INSERT INTO `TalkShowTexts` VALUES (316028, 3, 1, '艾米，不要在教室里吃饭。 你必须在饭厅吃饭。', 'Amy, don’t eat in the classroom. You must eat in the dining hall.', NULL, NULL, '19.3', '26.3');
INSERT INTO `TalkShowTexts` VALUES (316063, 14, 1, '我的牙齿痛。很糟糕！', 'I have a toothache. It’s terrible!', NULL, NULL, '46.5', '49.4');
INSERT INTO `TalkShowTexts` VALUES (316065, 8, 1, '您可以自愿参加课后学习计划来教孩子。', 'You could volunteer in an after-school study program to teach kids.', NULL, NULL, '27.8', '32.6');
