INSERT INTO `TalkShowTexts` VALUES (317177, 3, 1, '你昨天在公园吗？', 'Were you at the park yesterday?', NULL, NULL, '12.5', '15.7');
INSERT INTO `TalkShowTexts` VALUES (317177, 6, 1, '我昨天出门了。', 'I was out yesterday.', NULL, NULL, '22.9', '26.2');
INSERT INTO `TalkShowTexts` VALUES (317178, 4, 1, '非常好，谢谢你。你好吗？', 'Very well, thank you. And how are you?', NULL, NULL, '17.0', '21.2');
INSERT INTO `TalkShowTexts` VALUES (317178, 5, 1, '挺好的，谢谢。', 'Fine, thanks.', NULL, NULL, '21.5', '22.9');
INSERT INTO `TalkShowTexts` VALUES (317178, 10, 1, '哦，我想去阳光下玩。', 'Oh, I want to play in the sun.', NULL, NULL, '40.7', '44.8');
INSERT INTO `TalkShowTexts` VALUES (317178, 13, 1, '我和爷爷去上语文课了。', 'I was out with Grandpa for our Chinese lesson.', NULL, NULL, '51.8', '56.5');
INSERT INTO `TalkShowTexts` VALUES (317180, 7, 1, '不是的，以前是个小渔村。', 'no . It was a very small village then.', NULL, NULL, '16.6', '20.5');
INSERT INTO `TalkShowTexts` VALUES (317182, 4, 1, '我们都度过了非常快乐的一天。', 'We all had a very happy day.', NULL, NULL, '15.1', '17.5');
INSERT INTO `TalkShowTexts` VALUES (317183, 1, 1, '星期一，艾米打电话给奶奶。', 'On Monday, Amy phoned Grandma.', NULL, NULL, '0.6', '4.1');
INSERT INTO `TalkShowTexts` VALUES (317183, 5, 1, '我昨天煮了面条。', 'I cooked noodles yesterday.', NULL, NULL, '17.8', '20.4');
INSERT INTO `TalkShowTexts` VALUES (317183, 6, 1, '哦，真的吗？', 'Oh, really?', NULL, NULL, '21.0', '23.3');
INSERT INTO `TalkShowTexts` VALUES (317183, 7, 1, '是的，我帮妈妈的忙，我还洗了衣服。', 'Yes, I helped Mum and I washed clothes.', NULL, NULL, '24.2', '29.1');
INSERT INTO `TalkShowTexts` VALUES (317183, 9, 1, '山姆呢？', 'What about Sam?', NULL, NULL, '34.7', '37.0');
INSERT INTO `TalkShowTexts` VALUES (317183, 10, 1, '他没有帮妈妈的忙。', 'He didn’t help Mum.', NULL, NULL, '37.8', '39.8');
INSERT INTO `TalkShowTexts` VALUES (317183, 11, 1, '他玩电脑，看电视。', 'He played on the computer and he watched TV.', NULL, NULL, '40.1', '44.0');
INSERT INTO `TalkShowTexts` VALUES (317183, 14, 1, '哦，他是个好孩子。我爱他。', 'Oh, he is a good boy. I love him.', NULL, NULL, '52.8', '57.9');
INSERT INTO `TalkShowTexts` VALUES (317184, 1, 1, '我昨天和猫小姐聊天了。', 'I talked with Mrs Cat yesterday.', NULL, NULL, '0.5', '4.9');
INSERT INTO `TalkShowTexts` VALUES (317184, 2, 1, '我昨天和狗小姐一起散步。', 'I walkded with Miss Dog yesterday.', NULL, NULL, '6.1', '10.3');
INSERT INTO `TalkShowTexts` VALUES (317187, 3, 1, '他们还唱歌。', 'And they sang songs too.', NULL, NULL, '15.6', '18.6');
INSERT INTO `TalkShowTexts` VALUES (317188, 1, 1, '爸爸，上星期天我们去野餐了。', 'Dad, we had a picnic last Sunday.', NULL, NULL, '4.0', '7.5');
INSERT INTO `TalkShowTexts` VALUES (317188, 2, 1, '好玩吗？', 'Was it fun?', NULL, NULL, '7.7', '9.0');
INSERT INTO `TalkShowTexts` VALUES (317188, 3, 1, '是的，好玩。我们坐公交车去那边。', 'Yes, it was. We went there by bus.', NULL, NULL, '9.8', '14.1');
INSERT INTO `TalkShowTexts` VALUES (317188, 4, 1, '我们看到了一些鸟儿。它们唱得很动听。', 'And we saw some birds. They sang beautifully.', NULL, NULL, '15.4', '20.2');
INSERT INTO `TalkShowTexts` VALUES (317188, 5, 1, '我们吃了一些食物，喝了一些饮料。', 'We ate some food and drank some drinks.', NULL, NULL, '21.1', '24.8');
INSERT INTO `TalkShowTexts` VALUES (317188, 6, 1, '我们玩了游戏。', 'We played games.', NULL, NULL, '25.3', '27.2');
INSERT INTO `TalkShowTexts` VALUES (317188, 7, 1, '哦，你们玩得很开心！', 'Oh, you had a good time!', NULL, NULL, '27.5', '30.4');
INSERT INTO `TalkShowTexts` VALUES (317188, 12, 1, '哦，你们度过了忙碌的一天！', 'Oh, you had a busy day!', NULL, NULL, '40.1', '43.2');
INSERT INTO `TalkShowTexts` VALUES (317190, 1, 1, '野炊怎么样。', 'Tell me about your picnic.', NULL, NULL, '3.1', '5.8');
INSERT INTO `TalkShowTexts` VALUES (317190, 2, 1, '超级棒。', 'It was great.', NULL, NULL, '5.9', '7.7');
INSERT INTO `TalkShowTexts` VALUES (317190, 4, 1, '吃了很多美味的食物。', 'We ate lots of delicious food.', NULL, NULL, '9.8', '12.6');
INSERT INTO `TalkShowTexts` VALUES (317190, 6, 1, '我照了一些照片儿，做成了一张海报。', 'I took some pictures and I made a poster.', NULL, NULL, '16.0', '19.9');
INSERT INTO `TalkShowTexts` VALUES (317191, 4, 1, '我弹了钢琴，钢琴，', 'I played the piano, the piano,', NULL, NULL, '26.9', '31.4');
INSERT INTO `TalkShowTexts` VALUES (317192, 1, 1, '欢迎来到中国！', 'Welcome to China!', NULL, NULL, '6.9', '9.1');
INSERT INTO `TalkShowTexts` VALUES (317193, 1, 1, '看。这是我表哥大龙寄来的明信片。', 'Look. It’s a postcard from my cousin Dalong.', NULL, NULL, '1.6', '6.6');
INSERT INTO `TalkShowTexts` VALUES (317193, 7, 1, '听着。亲爱的大明，我在纽约度假。', 'Listen. \"Dear Daming, I’m on holiday in New York.\"', NULL, NULL, '29.4', '35.5');
INSERT INTO `TalkShowTexts` VALUES (317193, 8, 1, '他是坐飞机去的吗？', 'Did he travel by plane?', NULL, NULL, '38.0', '40.2');
INSERT INTO `TalkShowTexts` VALUES (317193, 9, 1, '是的，信上说，我是坐飞机来这儿的。', 'Yes, it says, \"I came here by plane.\"', NULL, NULL, '41.5', '46.3');
INSERT INTO `TalkShowTexts` VALUES (317193, 10, 1, '我上星期去了一场流行音乐会和一场篮球赛。', 'I went to a pop concert and a basketball game last week.', NULL, NULL, '48.6', '53.7');
INSERT INTO `TalkShowTexts` VALUES (317194, 2, 1, '恩，很愉快。我们去了地球。', 'Yes, we did. We went to the earth.', NULL, NULL, '17.7', '21.5');
INSERT INTO `TalkShowTexts` VALUES (317197, 3, 1, '杰克摔倒了。', 'Jack fell down.', NULL, NULL, '10.5', '12.0');
INSERT INTO `TalkShowTexts` VALUES (317197, 4, 1, '吉尔发现一个小镇。', 'Jill found a town.', NULL, NULL, '12.5', '14.4');
INSERT INTO `TalkShowTexts` VALUES (317198, 1, 1, '你的头怎么了，大明？', 'What happened to your head, Daming?', NULL, NULL, '3.9', '6.6');
INSERT INTO `TalkShowTexts` VALUES (317198, 2, 1, '哦，我和山姆昨天去骑自行车了。', 'Oh, Sam and I went for a bike ride yesterday.', NULL, NULL, '7.1', '11.2');
INSERT INTO `TalkShowTexts` VALUES (317198, 3, 1, '然后呢……？', 'And then...?', NULL, NULL, '12.0', '13.9');
INSERT INTO `TalkShowTexts` VALUES (317198, 4, 1, '然后我们又饿又渴。', 'And then we were hungry and thirsty.', NULL, NULL, '14.3', '17.3');
INSERT INTO `TalkShowTexts` VALUES (317198, 5, 1, '你买水了吗？', 'Did you buy some water?', NULL, NULL, '18.2', '20.2');
INSERT INTO `TalkShowTexts` VALUES (317198, 6, 1, '不，我们买了一个西瓜。', 'No, we bought a watermelon.', NULL, NULL, '20.4', '23.6');
INSERT INTO `TalkShowTexts` VALUES (317198, 7, 1, '山姆把西瓜放在自行车上带走。', 'Sam carried the watermelon on the bike.', NULL, NULL, '24.5', '28.1');
INSERT INTO `TalkShowTexts` VALUES (317198, 8, 1, '你们从自行车上摔下来了吗？', 'Did you fall off your bike?', NULL, NULL, '28.8', '31.1');
INSERT INTO `TalkShowTexts` VALUES (317198, 9, 1, '不，山姆从他的自行车上摔下来了。', 'No, Sam fell off his bike.', NULL, NULL, '31.9', '35.1');
INSERT INTO `TalkShowTexts` VALUES (317198, 10, 1, '我撞到了头！', 'And I bumped my head!', NULL, NULL, '35.5', '37.6');
INSERT INTO `TalkShowTexts` VALUES (317198, 11, 1, '然后他带着我和西瓜去了医院。', 'Then he took me and the watermelon to the hospital.', NULL, NULL, '38.0', '41.3');
INSERT INTO `TalkShowTexts` VALUES (317199, 1, 1, '我们昨天去了野炊。', 'We had a picnic yesterday.', NULL, NULL, '5.2', '7.9');
INSERT INTO `TalkShowTexts` VALUES (317200, 1, 1, '萨姆昨天吃了很多巧克力，', 'Sam had lots of chocolate yesterday,', NULL, NULL, '6.4', '9.8');
INSERT INTO `TalkShowTexts` VALUES (317200, 2, 1, '所以他今天胃疼。', 'so today he’s got a stomach ache.', NULL, NULL, '9.9', '13.3');
INSERT INTO `TalkShowTexts` VALUES (317200, 5, 1, '艾米昨天感冒了，', 'Amy had a cold yesterday,', NULL, NULL, '26.8', '30.0');
INSERT INTO `TalkShowTexts` VALUES (317200, 7, 1, '玲玲昨天头疼，', 'Lingling had a headache yesterday,', NULL, NULL, '35.6', '39.2');
INSERT INTO `TalkShowTexts` VALUES (317200, 8, 1, '今天就发烧了。', 'and today she’s got a fever.', NULL, NULL, '39.3', '42.7');
INSERT INTO `TalkShowTexts` VALUES (317202, 4, 1, '还在空中俯瞰了这座城市。', 'and saw the city from  above the ground.', NULL, NULL, '15.7', '20.1');
INSERT INTO `TalkShowTexts` VALUES (317203, 11, 1, '艾米，看那些冰淇淋。', 'Amy, look at those ice creams!', NULL, NULL, '39.5', '43.4');
INSERT INTO `TalkShowTexts` VALUES (317203, 15, 1, '我们现在要回家了，约翰。跟我们走吧。', 'We’re going home now, John. Come with us.', NULL, NULL, '59.6', '63.8');
INSERT INTO `TalkShowTexts` VALUES (317203, 16, 1, '这是我们的巴士！把你的冰淇淋吃完，玲玲。', 'There’s our bus! Finish your ice cream, Lingling.', NULL, NULL, '67.4', '71.9');
INSERT INTO `TalkShowTexts` VALUES (317203, 17, 1, '等等我！', 'Wait for me!', NULL, NULL, '74.8', '76.4');
INSERT INTO `TalkShowTexts` VALUES (317203, 18, 1, '快点，玲玲。跑快点！', 'Hurry up, Lingling. Run!', NULL, NULL, '78.8', '82.5');
INSERT INTO `TalkShowTexts` VALUES (317203, 19, 1, '哦不！我的冰淇淋掉了！', 'Oh no! I dropped my ice cream!', NULL, NULL, '86.4', '91.0');
INSERT INTO `TalkShowTexts` VALUES (317204, 3, 1, '她有没有发邮件给你？', 'Did she send you an email?', NULL, NULL, '9.1', '11.8');
INSERT INTO `TalkShowTexts` VALUES (317205, 3, 1, '我们在公园看到了约翰。', 'We met John in the park.', NULL, NULL, '11.9', '14.6');
INSERT INTO `TalkShowTexts` VALUES (317205, 7, 1, '我跑着赶公交车。', 'I ran to the bus.', NULL, NULL, '25.9', '28.6');
INSERT INTO `TalkShowTexts` VALUES (317205, 10, 1, '爱你的 ，玲玲。', 'Love, Lingling', NULL, NULL, '38.3', '40.5');
INSERT INTO `TalkShowTexts` VALUES (317207, 6, 1, '额，在这里！', 'Er...Here!', NULL, NULL, '18.9', '22.3');
INSERT INTO `TalkShowTexts` VALUES (317208, 2, 1, '我们需要为野餐购置食物。', 'We need food for our picnic.', NULL, NULL, '2.9', '6.2');
INSERT INTO `TalkShowTexts` VALUES (317208, 3, 1, '我写个购物清单。', 'I’ll make a shopping list.', NULL, NULL, '6.4', '9.0');
INSERT INTO `TalkShowTexts` VALUES (317208, 4, 1, '首先，我们要买六根香蕉。', 'First, we need six bananas.', NULL, NULL, '9.1', '12.8');
INSERT INTO `TalkShowTexts` VALUES (317208, 6, 1, '好的，我们买四个苹果。', 'OK, we can buy four apples.', NULL, NULL, '16.1', '20.6');
INSERT INTO `TalkShowTexts` VALUES (317208, 8, 1, '哦糟糕！我把购物清单丢了。', 'Oh no! I lost the list.', NULL, NULL, '25.8', '30.0');
INSERT INTO `TalkShowTexts` VALUES (317208, 11, 1, '她说买多少奶酪？', 'How much cheese did she say?', NULL, NULL, '36.6', '40.0');
INSERT INTO `TalkShowTexts` VALUES (317208, 18, 1, '我们没买香蕉。', 'We didn’t buy any bananas.', NULL, NULL, '59.3', '62.9');
INSERT INTO `TalkShowTexts` VALUES (317210, 2, 1, '我们可以用那边的袋子。', 'We can use the bag over there.', NULL, NULL, '4.8', '8.3');
