INSERT INTO `TalkShowTexts` VALUES (317210, 6, 1, '一斤。', 'Half a kilo.', NULL, NULL, '24.4', '26.2');
INSERT INTO `TalkShowTexts` VALUES (317210, 7, 1, '我们还买了不少巧克力！', 'And we also bought a lot of chocolate!', NULL, NULL, '26.3', '30.0');
INSERT INTO `TalkShowTexts` VALUES (317213, 4, 1, '我们去了好多地方。', 'We visited lots of places.', NULL, NULL, '9.0', '12.0');
INSERT INTO `TalkShowTexts` VALUES (317213, 6, 1, '我们去了英国博物馆。', 'We went to the British Museum.', NULL, NULL, '14.0', '16.7');
INSERT INTO `TalkShowTexts` VALUES (317213, 8, 1, '你们怎么去的这些地方啊？', 'How did you go to these places?', NULL, NULL, '21.2', '24.1');
INSERT INTO `TalkShowTexts` VALUES (317213, 12, 1, '但是她最喜欢坐公交！', 'But she liked the bus ride best!', NULL, NULL, '32.6', '35.7');
INSERT INTO `TalkShowTexts` VALUES (317213, 13, 1, '然后我们在河边乘船转了转。', 'Then we took a boat trip along the river.', NULL, NULL, '38.3', '41.8');
INSERT INTO `TalkShowTexts` VALUES (317213, 14, 1, '花了我们80分钟。', 'It took us one hour and twenty minutes.', NULL, NULL, '43.7', '47.1');
INSERT INTO `TalkShowTexts` VALUES (317215, 1, 1, '大明和他爸爸周末去了长城。', 'Daming and his father went to the Great Wall at the weekend.', NULL, NULL, '0.5', '6.4');
INSERT INTO `TalkShowTexts` VALUES (317215, 3, 1, '他们上午十点到的那里。', 'They arrived there at ten o’clock in the morning.', NULL, NULL, '17.5', '22.0');
