INSERT INTO `TalkShowTexts` VALUES (317215, 7, 1, '他们给山拍了照片。', 'They took photos of the mountains ', NULL, NULL, '49.8', '53.1');
INSERT INTO `TalkShowTexts` VALUES (317215, 8, 1, '那山上布满了鲜花和绿色植物。', 'with beautiful flowers and green plants.', NULL, NULL, '53.1', '57.6');
INSERT INTO `TalkShowTexts` VALUES (317216, 8, 1, '见了许多地方。', 'I saw lots of places.', NULL, NULL, '19.0', '22.3');
INSERT INTO `TalkShowTexts` VALUES (317217, 1, 1, '山姆，你喜欢这条短裤吗？', 'Do you like this pair of shorts, Sam?', NULL, NULL, '1.8', '5.0');
INSERT INTO `TalkShowTexts` VALUES (317218, 3, 1, '不要吵！怎么了？', 'Don’t argue! What’s the matter?', NULL, NULL, '7.8', '11.3');
INSERT INTO `TalkShowTexts` VALUES (317218, 4, 1, '山姆拿我的T恤。', 'Sam took my T-shirt.', NULL, NULL, '11.4', '14.0');
INSERT INTO `TalkShowTexts` VALUES (317218, 15, 1, '她还没有穿。', 'She didn’t wear it.', NULL, NULL, '44.6', '47.0');
INSERT INTO `TalkShowTexts` VALUES (317218, 18, 1, '没关系。', 'That’s OK.', NULL, NULL, '52.9', '54.6');
INSERT INTO `TalkShowTexts` VALUES (317220, 4, 1, '我的新运动鞋。', 'My new sports shoes.', NULL, NULL, '11.4', '15.0');
INSERT INTO `TalkShowTexts` VALUES (317220, 7, 1, '嘿，那就是我的包！', 'Hey, that’s my bag!', NULL, NULL, '26.7', '29.5');
INSERT INTO `TalkShowTexts` VALUES (317222, 1, 1, '你能看到几张脸？', 'How many faces can you see?', NULL, NULL, '0.9', '5.6');
INSERT INTO `TalkShowTexts` VALUES (317223, 1, 1, '上课啦。', 'Class begins.', NULL, NULL, '2.4', '4.8');
