INSERT INTO `TalkShowTexts` VALUES (317223, 3, 1, '好的。', 'All right.', NULL, NULL, '9.4', '11.0');
INSERT INTO `TalkShowTexts` VALUES (317223, 6, 1, '十一，十二，十三，十四，十五，', 'eleven, twelve, thirteen, fourteen, fifteen,', NULL, NULL, '26.4', '33.4');
