INSERT INTO `TalkShowTexts` VALUES (317224, 1, 1, '数字们齐聚大派对。', 'Numbers are having a party.', NULL, NULL, '5.0', '8.8');
INSERT INTO `TalkShowTexts` VALUES (317224, 3, 1, '三十，', 'thirty,', NULL, NULL, '11.3', '12.4');
