INSERT INTO `TalkShowTexts` VALUES (318261, 3, 1, '十四，十五，十六。', 'Fourteen, fifteen and sixteen.', NULL, NULL, '15.7', '19.2');
INSERT INTO `TalkShowTexts` VALUES (318261, 5, 1, '十七，十八，十九。', 'Seventeen, eighteen and nineteen.', NULL, NULL, '23.0', '26.5');
INSERT INTO `TalkShowTexts` VALUES (318263, 5, 1, '哦。那明天你会把你的球带上吗？', 'Oh. Will you take your ball tomorrow?', NULL, NULL, '15.6', '20.3');
INSERT INTO `TalkShowTexts` VALUES (318263, 7, 1, '为什么？', 'Why not?', NULL, NULL, '22.8', '24.4');
INSERT INTO `TalkShowTexts` VALUES (318265, 1, 1, '我们下周要放假了。', 'We are on holiday next week.', NULL, NULL, '0.0', '3.2');
INSERT INTO `TalkShowTexts` VALUES (318266, 2, 1, '周一会吃水果，', 'will eat fruit on Monday,', NULL, NULL, '10.5', '12.8');
INSERT INTO `TalkShowTexts` VALUES (318266, 3, 1, '周二会玩球，', 'will play ball on Tuesday', NULL, NULL, '13.6', '16.1');
INSERT INTO `TalkShowTexts` VALUES (318266, 4, 1, '周三会唱歌，', 'will sing on Wednesday,', NULL, NULL, '17.2', '19.6');
INSERT INTO `TalkShowTexts` VALUES (318266, 5, 1, '周四会跳舞，', 'will dance on Thursday,', NULL, NULL, '20.6', '22.9');
INSERT INTO `TalkShowTexts` VALUES (318267, 4, 1, '跺跺跺。', 'stamp, stamp, stamp.', NULL, NULL, '13.3', '15.2');
INSERT INTO `TalkShowTexts` VALUES (318268, 3, 1, '我们要画什么呢？', 'What shall we draw?', NULL, NULL, '13.0', '14.7');
INSERT INTO `TalkShowTexts` VALUES (318268, 6, 1, '让我们在电脑上画一只怪物。', 'Let’s draw a monster on the computer.', NULL, NULL, '21.4', '25.1');
INSERT INTO `TalkShowTexts` VALUES (318268, 10, 1, '这是它的胳膊。', 'Here are his arms.', NULL, NULL, '35.8', '38.4');
INSERT INTO `TalkShowTexts` VALUES (318270, 4, 1, '有二十四只手。', 'There are twenty-four hands.', NULL, NULL, '11.4', '15.1');
INSERT INTO `TalkShowTexts` VALUES (318272, 1, 1, '这儿有猴子吗？', 'Are there any monkeys here?', NULL, NULL, '2.3', '5.0');
INSERT INTO `TalkShowTexts` VALUES (318273, 1, 1, '艾米，我能问你个问题吗？', 'Amy, can I ask you a question?', NULL, NULL, '0.0', '4.1');
INSERT INTO `TalkShowTexts` VALUES (318273, 3, 1, '你们班有很多孩子吗？', 'Are there many children in your class?', NULL, NULL, '6.8', '10.3');
INSERT INTO `TalkShowTexts` VALUES (318273, 4, 1, '是的，有41个。', 'Yes, there are forty-one.', NULL, NULL, '10.5', '13.8');
INSERT INTO `TalkShowTexts` VALUES (318273, 10, 1, '不，没有。大概只有20个。', 'No, there aren’t. There are about twenty.', NULL, NULL, '31.2', '36.2');
INSERT INTO `TalkShowTexts` VALUES (318273, 11, 1, '所以在中国我们才有更多朋友。', 'So we’ve got more friends in China.', NULL, NULL, '36.8', '41.4');
INSERT INTO `TalkShowTexts` VALUES (318274, 6, 1, '二十一，二十二，二十三，二十四，二十五，二十六。', 'twenty-one, twenty-two, twenty-three, twenty-four, twenty-five, twenty-six.', NULL, NULL, '48.1', '58.1');
INSERT INTO `TalkShowTexts` VALUES (318276, 3, 1, '三十只大象成一行。', 'Thirty elephants in a row.', NULL, NULL, '11.3', '13.9');
INSERT INTO `TalkShowTexts` VALUES (318278, 2, 1, '今天是家长日。', 'It’s Parents’ Day today.', NULL, NULL, '2.6', '5.1');
INSERT INTO `TalkShowTexts` VALUES (318278, 3, 1, '我们要去你的老师谈话。', 'We’re going to speak to your teacher.', NULL, NULL, '5.4', '8.4');
INSERT INTO `TalkShowTexts` VALUES (318278, 13, 1, '听到这些我很高兴。', 'I’m happy with that.', NULL, NULL, '35.2', '37.9');
INSERT INTO `TalkShowTexts` VALUES (318280, 1, 1, '我们拿到了玲玲的报告。', 'We’ve got Lingling’s report.', NULL, NULL, '0.0', '3.3');
INSERT INTO `TalkShowTexts` VALUES (318280, 4, 1, '她很擅长英语。', 'She’s quite good at English.', NULL, NULL, '8.9', '11.6');
INSERT INTO `TalkShowTexts` VALUES (318280, 5, 1, '她很努力学科学。', 'She tries hard at Science.', NULL, NULL, '11.9', '15.3');
INSERT INTO `TalkShowTexts` VALUES (318280, 6, 1, '她在体育课上跑得很快。', 'She runs fast in PE.', NULL, NULL, '15.7', '18.8');
INSERT INTO `TalkShowTexts` VALUES (318280, 7, 1, '她很擅长语文。', 'She’s very good at Chinese.', NULL, NULL, '19.6', '22.5');
INSERT INTO `TalkShowTexts` VALUES (318280, 8, 1, '但她没有努力学艺术。', 'But she doesn’t work hard at Art.', NULL, NULL, '22.8', '26.8');
INSERT INTO `TalkShowTexts` VALUES (318281, 2, 1, '这是一只懒惰的猫。', 'And this is a lazy cat.', NULL, NULL, '7.4', '9.6');
INSERT INTO `TalkShowTexts` VALUES (318282, 2, 1, '它很漂亮。', 'It’s beautiful.', NULL, NULL, '4.6', '6.3');
INSERT INTO `TalkShowTexts` VALUES (318282, 3, 1, '那时候也很美。', 'And it was also beautiful then.', NULL, NULL, '9.1', '12.0');
INSERT INTO `TalkShowTexts` VALUES (318283, 1, 1, '他们是谁啊？玲玲。', 'Who are they, Lingling?', NULL, NULL, '0.0', '2.9');
INSERT INTO `TalkShowTexts` VALUES (318283, 2, 1, '他们是我的祖父母。', 'They are my grandparents.', NULL, NULL, '3.6', '6.2');
INSERT INTO `TalkShowTexts` VALUES (318283, 3, 1, '他们那时可真年轻啊。', 'They were very young then.', NULL, NULL, '6.4', '8.8');
INSERT INTO `TalkShowTexts` VALUES (318283, 4, 1, '是啊。但他们现在很老了。', 'Yes. But they are very old now.', NULL, NULL, '8.9', '13.3');
INSERT INTO `TalkShowTexts` VALUES (318284, 3, 1, '昨天我在北京。', 'And I was in Beijing yesterday.', NULL, NULL, '6.1', '10.1');
INSERT INTO `TalkShowTexts` VALUES (318285, 8, 1, '真是有趣！', 'It was fun!', NULL, NULL, '27.6', '29.4');
INSERT INTO `TalkShowTexts` VALUES (318286, 2, 1, '我以前胖，但是现在瘦。', 'I was fat, but now I’m thin.', NULL, NULL, '8.1', '12.4');
INSERT INTO `TalkShowTexts` VALUES (318288, 1, 1, '嗨！我很担心。我找不到大明。', 'Hi! I’m worried. I can’t find Daming!', NULL, NULL, '0.4', '6.1');
INSERT INTO `TalkShowTexts` VALUES (318288, 2, 1, '我们刚刚在一楼。', 'We were on the first floor.', NULL, NULL, '6.9', '9.5');
INSERT INTO `TalkShowTexts` VALUES (318288, 7, 1, '我记得大明在那里！', 'And Daming was there! I remember!', NULL, NULL, '22.0', '25.8');
INSERT INTO `TalkShowTexts` VALUES (318288, 13, 1, '母亲节快乐！', 'Happy Mother’s Day!', NULL, NULL, '43.6', '46.0');
INSERT INTO `TalkShowTexts` VALUES (318290, 1, 1, '蔬菜在哪？', 'Where are the vegetables?', NULL, NULL, '1.5', '3.8');
INSERT INTO `TalkShowTexts` VALUES (318290, 3, 1, '上周日它们在这。', 'They were here last Sunday.', NULL, NULL, '5.8', '8.2');
INSERT INTO `TalkShowTexts` VALUES (318291, 5, 1, '食物在一楼。', 'The food was on the first floor.', NULL, NULL, '25.2', '27.9');
INSERT INTO `TalkShowTexts` VALUES (318291, 8, 1, '玩具在二楼。', 'The toys were on the second floor.', NULL, NULL, '33.5', '36.2');
INSERT INTO `TalkShowTexts` VALUES (318293, 5, 1, '亲爱的艾米，', 'Dear Amy,', NULL, NULL, '18.4', '20.0');
INSERT INTO `TalkShowTexts` VALUES (318293, 7, 1, '我很好。', 'I’m very well.', NULL, NULL, '21.7', '23.3');
INSERT INTO `TalkShowTexts` VALUES (318293, 12, 1, '这是我生日聚会的一张照片。', 'Here is a photo of my birthday party.', NULL, NULL, '35.1', '38.4');
INSERT INTO `TalkShowTexts` VALUES (318293, 19, 1, '尽快写信给我哦。', 'Write to me soon.', NULL, NULL, '57.4', '59.2');
INSERT INTO `TalkShowTexts` VALUES (318293, 20, 1, '跟我说说你的朋友们吧。', 'Tell me about your friends.', NULL, NULL, '59.6', '62.0');
INSERT INTO `TalkShowTexts` VALUES (318293, 21, 1, '爱你的，露西。', 'Love, Lucy.', NULL, NULL, '62.3', '64.0');
INSERT INTO `TalkShowTexts` VALUES (318297, 1, 1, '万岁，万岁，我洗了我的衬衫！', 'Hooray, hooray, I washed my shirt!', NULL, NULL, '4.6', '9.2');
INSERT INTO `TalkShowTexts` VALUES (318298, 1, 1, '昨天，我打扫了我的房间。', 'Yesterday, I cleaned my room.', NULL, NULL, '1.0', '5.1');
INSERT INTO `TalkShowTexts` VALUES (318298, 2, 1, '我做完了我的作业。', 'I finished my homework.', NULL, NULL, '5.9', '8.9');
INSERT INTO `TalkShowTexts` VALUES (318298, 3, 1, '我的裤子很脏了。', 'My trousers were very dirty.', NULL, NULL, '9.9', '13.0');
INSERT INTO `TalkShowTexts` VALUES (318300, 1, 1, '昨天，斯马特先生做了面。', 'Yesterday, Mr Smart cooked noodles.', NULL, NULL, '0.8', '4.7');
INSERT INTO `TalkShowTexts` VALUES (318300, 3, 1, '斯马特太太打了电话给奶奶。', 'Ms Smart phoned Grandma.', NULL, NULL, '7.5', '10.5');
INSERT INTO `TalkShowTexts` VALUES (318300, 5, 1, '艾米画了一幅画。', 'Amy painted a picture.', NULL, NULL, '14.5', '17.0');
INSERT INTO `TalkShowTexts` VALUES (318303, 4, 1, '但昨天她没有步行去上学。', 'But she didn’t walk to school yesterday.', NULL, NULL, '14.1', '17.8');
INSERT INTO `TalkShowTexts` VALUES (318303, 7, 1, '昨天是国庆节！', 'Yesterday was National Day!', NULL, NULL, '26.5', '30.1');
INSERT INTO `TalkShowTexts` VALUES (318305, 7, 1, '多么无聊的一天啊！', 'What a boring day!', NULL, NULL, '25.0', '27.8');
INSERT INTO `TalkShowTexts` VALUES (318306, 4, 1, '我待在了家里。', 'I stayed at home.', NULL, NULL, '13.8', '15.5');
INSERT INTO `TalkShowTexts` VALUES (318308, 1, 1, '中国人发明了很多重要的东西。', 'Chinese people invented many important things.', NULL, NULL, '1.2', '6.1');
INSERT INTO `TalkShowTexts` VALUES (318308, 3, 1, '是的。中国人发明了纸。', 'Yes. Chinese people invented paper.', NULL, NULL, '10.5', '15.2');
INSERT INTO `TalkShowTexts` VALUES (318308, 4, 1, '中国人还发明了印刷术。', 'Chinese people invented printing, too.', NULL, NULL, '16.1', '20.0');
INSERT INTO `TalkShowTexts` VALUES (318308, 5, 1, '我们印刷书和报纸。', 'We print books and newspapers.', NULL, NULL, '20.7', '24.6');
INSERT INTO `TalkShowTexts` VALUES (318308, 6, 1, '没错。', 'That’s right.', NULL, NULL, '24.8', '26.2');
INSERT INTO `TalkShowTexts` VALUES (318308, 8, 1, '看，它在椅子中间。', 'Look, it’s between the chairs.', NULL, NULL, '37.9', '41.2');
INSERT INTO `TalkShowTexts` VALUES (318309, 1, 1, '快看那只老鼠。', 'Look at the mouse.', NULL, NULL, '2.0', '4.1');
INSERT INTO `TalkShowTexts` VALUES (318310, 1, 1, '看这个在自行车旁边的男人。', 'Look at the man beside the bicycle.', NULL, NULL, '1.8', '5.0');
INSERT INTO `TalkShowTexts` VALUES (318313, 1, 1, '你们昨天参加学校组织的旅行了吗？', 'Did you go on a school trip yesterday?', NULL, NULL, '1.0', '4.2');
INSERT INTO `TalkShowTexts` VALUES (318313, 2, 1, '是的。我们去了长城。', 'Yes. We went to the Great Wall.', NULL, NULL, '4.7', '8.8');
INSERT INTO `TalkShowTexts` VALUES (318313, 3, 1, '我们花了五十分钟爬到顶部。', 'It took us fifty minutes to climb to the top.', NULL, NULL, '9.5', '13.8');
INSERT INTO `TalkShowTexts` VALUES (318313, 4, 1, '我们看到了很多山。', 'And we saw lots of mountains.', NULL, NULL, '14.1', '17.4');
INSERT INTO `TalkShowTexts` VALUES (318313, 5, 1, '我们吃了糖。', 'We ate candies.', NULL, NULL, '18.1', '20.1');
INSERT INTO `TalkShowTexts` VALUES (318313, 6, 1, '我们看到了一些漂亮的植物。', 'And we saw some beautiful plants.', NULL, NULL, '20.4', '24.0');
INSERT INTO `TalkShowTexts` VALUES (318313, 7, 1, '所以，你们过得很开心。', 'So, you had a good time.', NULL, NULL, '24.1', '26.5');
INSERT INTO `TalkShowTexts` VALUES (318313, 8, 1, '是的。我们给你买了一份礼物。', 'Yes. And we bought you a present.', NULL, NULL, '26.6', '30.4');
INSERT INTO `TalkShowTexts` VALUES (318315, 4, 1, '我穿了暖和的衣服。', 'I wore warm clothes.', NULL, NULL, '8.5', '10.8');
INSERT INTO `TalkShowTexts` VALUES (318315, 5, 1, '冰非常冷。', 'The ice was very cold.', NULL, NULL, '11.1', '13.2');
INSERT INTO `TalkShowTexts` VALUES (318315, 6, 1, '起先，我摔倒了。', 'At first, I fell over.', NULL, NULL, '13.6', '15.9');
INSERT INTO `TalkShowTexts` VALUES (318315, 7, 1, '但伤得不重。', 'But it didn’t hurt a lot.', NULL, NULL, '16.1', '17.9');
INSERT INTO `TalkShowTexts` VALUES (318315, 8, 1, '然后我会滑冰了。', 'Then I was able to skate.', NULL, NULL, '18.1', '20.4');
INSERT INTO `TalkShowTexts` VALUES (318318, 1, 1, '很久很久以前，有一个好男孩。', 'Long, long ago, there was a good boy.', NULL, NULL, '1.0', '4.9');
INSERT INTO `TalkShowTexts` VALUES (318318, 4, 1, '他有一支神奇的画笔。', 'He had a magic paintbrush.', NULL, NULL, '10.4', '13.5');
INSERT INTO `TalkShowTexts` VALUES (318318, 5, 1, '这位老奶奶没有东西吃。', 'This old woman didn’t have food. ', NULL, NULL, '14.4', '17.4');
INSERT INTO `TalkShowTexts` VALUES (318318, 7, 1, '然后食物变成了真的。', 'Then the food became real.', NULL, NULL, '21.3', '24.0');
INSERT INTO `TalkShowTexts` VALUES (318318, 8, 1, '有一个坏人。', 'There was a bad man. ', NULL, NULL, '25.1', '27.5');
INSERT INTO `TalkShowTexts` VALUES (318318, 9, 1, '他拿走了马良神奇的画笔。', 'He took Ma Liang’s magic paintbrush.', NULL, NULL, '28.3', '32.2');
INSERT INTO `TalkShowTexts` VALUES (318318, 12, 1, '但是它没有变成金子。', 'But it didn’t become gold. ', NULL, NULL, '40.6', '43.1');
INSERT INTO `TalkShowTexts` VALUES (318319, 2, 1, '她没有把花带走。', 'She didn’t take the flowers away.', NULL, NULL, '9.5', '14.1');
INSERT INTO `TalkShowTexts` VALUES (318319, 3, 1, '她没有把叶子带走。', 'She didn’t take the leaves away.', NULL, NULL, '14.3', '18.9');
INSERT INTO `TalkShowTexts` VALUES (318319, 4, 1, '她只把自己的画带走了。', 'She only took her painting away.', NULL, NULL, '19.0', '23.8');
INSERT INTO `TalkShowTexts` VALUES (318323, 1, 1, '出什么事了，艾米？', 'What’s the matter,Amy?', NULL, NULL, '1.9', '4.0');
INSERT INTO `TalkShowTexts` VALUES (318323, 2, 1, '我弄丢了我最喜欢的玩偶。', 'I lost my favourite doll.', NULL, NULL, '4.3', '7.4');
INSERT INTO `TalkShowTexts` VALUES (318323, 3, 1, '我找不到它了。', 'I can’t find it.', NULL, NULL, '7.5', '9.8');
INSERT INTO `TalkShowTexts` VALUES (318325, 2, 1, '我把钥匙弄丢了。', 'I lost my key.', NULL, NULL, '7.2', '9.5');
INSERT INTO `TalkShowTexts` VALUES (318325, 5, 1, '你在你包里找过了吗？', 'Did you look for it in your bag?', NULL, NULL, '14.3', '16.8');
INSERT INTO `TalkShowTexts` VALUES (318328, 1, 1, '看，艾米，一些英国孩子上周到了中国。', 'Look, Amy,some English children came to China last week.', NULL, NULL, '3.0', '9.3');
INSERT INTO `TalkShowTexts` VALUES (318328, 4, 1, '他们什么时候来的？', 'When did they come?', NULL, NULL, '15.4', '17.2');
INSERT INTO `TalkShowTexts` VALUES (318328, 6, 1, '看，这个女孩拿到了金奖杯。', 'Look,this girl won the gold cup.', NULL, NULL, '21.9', '25.8');
INSERT INTO `TalkShowTexts` VALUES (318328, 9, 1, '我也可以演奏长笛。', 'I can play the flute,too.', NULL, NULL, '30.9', '33.4');
INSERT INTO `TalkShowTexts` VALUES (318328, 10, 1, '所以要多练习。', 'So practise more.', NULL, NULL, '35.7', '37.9');
INSERT INTO `TalkShowTexts` VALUES (318328, 11, 1, '然后你也可以赢得比赛。', 'Then you will win a competition,too.', NULL, NULL, '38.4', '41.6');
INSERT INTO `TalkShowTexts` VALUES (318329, 4, 1, '我拍了你一张照片。', 'I took a picture of you.', NULL, NULL, '11.5', '13.7');
INSERT INTO `TalkShowTexts` VALUES (318330, 1, 1, '艾米，你看上去很累。', 'Amy,you look tired.', NULL, NULL, '1.9', '4.8');
INSERT INTO `TalkShowTexts` VALUES (318330, 6, 1, '我练习了很久。', 'I practised a lot.', NULL, NULL, '18.0', '20.5');
INSERT INTO `TalkShowTexts` VALUES (318333, 1, 1, '你的头怎么了，大明？', 'What happened to your head,Daming?', NULL, NULL, '1.3', '4.1');
INSERT INTO `TalkShowTexts` VALUES (318333, 2, 1, '哦，山姆和我昨天去骑自行车。', 'Oh,Sam and I went for a bike ride yesterday.', NULL, NULL, '4.4', '9.4');
INSERT INTO `TalkShowTexts` VALUES (318333, 4, 1, '然后我们都很饿很渴。', 'And then we were hungry and thirsty.', NULL, NULL, '11.5', '15.7');
INSERT INTO `TalkShowTexts` VALUES (318333, 5, 1, '你们买了水吗？', 'Did you buy some water?', NULL, NULL, '16.0', '18.3');
INSERT INTO `TalkShowTexts` VALUES (318333, 6, 1, '没有，我们买了一个西瓜。', 'No,we bought a watermelon.', NULL, NULL, '18.4', '22.1');
INSERT INTO `TalkShowTexts` VALUES (318333, 7, 1, '山姆把西瓜挂在自行车上。', 'Sam carried the watermelon on the bike.', NULL, NULL, '22.6', '26.6');
INSERT INTO `TalkShowTexts` VALUES (318333, 9, 1, '不是，是山姆摔下了自行车。', 'No,Sam fell off his bike.', NULL, NULL, '29.5', '33.0');
INSERT INTO `TalkShowTexts` VALUES (318333, 10, 1, '然后我撞到了头。', 'And I bumped my head.', NULL, NULL, '33.5', '36.4');
INSERT INTO `TalkShowTexts` VALUES (318335, 4, 1, '我剪到了手指。', 'I cut my finger.', NULL, NULL, '15.0', '17.2');
INSERT INTO `TalkShowTexts` VALUES (318336, 3, 1, '别掉下来摔伤你的膝盖。', 'Don’t fall and hurt your knees.', NULL, NULL, '14.4', '16.6');
INSERT INTO `TalkShowTexts` VALUES (318336, 7, 1, '别切到你的小手。', 'Don’t cut your little hand.', NULL, NULL, '24.3', '26.4');
INSERT INTO `TalkShowTexts` VALUES (318337, 1, 1, '我早晨起得非常早。', 'I get up very early every morning.', NULL, NULL, '4.0', '8.5');
INSERT INTO `TalkShowTexts` VALUES (318337, 2, 1, '我起床非常晚。', 'I get up very late.', NULL, NULL, '11.9', '14.5');
INSERT INTO `TalkShowTexts` VALUES (318337, 3, 1, '我白天一直睡觉。', 'I sleep during the day.', NULL, NULL, '14.7', '17.3');
INSERT INTO `TalkShowTexts` VALUES (318338, 5, 1, '我玩电脑游戏。', 'I played computer games.', NULL, NULL, '19.8', '22.2');
INSERT INTO `TalkShowTexts` VALUES (318338, 7, 1, '把这个药吃了，多喝水。', 'Take this medicine,Drink a lot of water.', NULL, NULL, '25.4', '30.2');
INSERT INTO `TalkShowTexts` VALUES (318338, 8, 1, '早点上床睡觉。做些运动。', 'Go to bed early.And do some exercise.', NULL, NULL, '30.6', '35.6');
INSERT INTO `TalkShowTexts` VALUES (318340, 2, 1, '每天都吃些蔬菜。', 'Eat vegetables every day.', NULL, NULL, '4.9', '7.9');
INSERT INTO `TalkShowTexts` VALUES (318340, 4, 1, '喝很多水或者果汁。', 'Drink lots of water or juice.', NULL, NULL, '11.0', '14.2');
INSERT INTO `TalkShowTexts` VALUES (318340, 7, 1, '永远都在吃东西之前和上厕所之后洗手。', 'Always wash your hands before you eat and after you go to the toilet.', NULL, NULL, '25.1', '31.0');
INSERT INTO `TalkShowTexts` VALUES (318343, 1, 1, '欢迎来到我们的工厂，孩子们！', 'Welcome to our factory, children!', NULL, NULL, '1.3', '4.6');
INSERT INTO `TalkShowTexts` VALUES (318343, 2, 1, '这些机器制作薯片。', 'These machines make crisps.', NULL, NULL, '11.9', '15.0');
INSERT INTO `TalkShowTexts` VALUES (318343, 4, 1, '它清洗土豆。', 'It washes the potatoes.', NULL, NULL, '22.1', '24.9');
INSERT INTO `TalkShowTexts` VALUES (318343, 5, 1, '看！这台机器切土豆。', 'Look! This machine cuts the potatoes.', NULL, NULL, '29.6', '35.0');
INSERT INTO `TalkShowTexts` VALUES (318343, 7, 1, '它们很危险。', 'They are dangerous.', NULL, NULL, '38.7', '40.5');
INSERT INTO `TalkShowTexts` VALUES (318343, 9, 1, '这台机器把薯片放到袋子里。', 'This machine puts the crisps in the bags.', NULL, NULL, '46.9', '50.9');
INSERT INTO `TalkShowTexts` VALUES (318343, 10, 1, '等等！你们是个很棒的班级。', 'Wait! You are a very good class.', NULL, NULL, '52.3', '56.3');
INSERT INTO `TalkShowTexts` VALUES (318343, 11, 1, '你们每个人都可以拿一包薯片。', 'You can each have a bag of crisps.', NULL, NULL, '56.5', '59.8');
INSERT INTO `TalkShowTexts` VALUES (318343, 12, 1, '哦，谢谢你，先生！', 'Oh, thank you, sir!', NULL, NULL, '60.7', '64.3');
INSERT INTO `TalkShowTexts` VALUES (318348, 14, 1, '现在我也可以给你发电子邮件了。', 'Now I can send emails to you, too.', NULL, NULL, '50.5', '55.2');
INSERT INTO `TalkShowTexts` VALUES (318350, 4, 1, '哇！它很贵。', 'Wow! It’s expensive.', NULL, NULL, '12.1', '16.0');
INSERT INTO `TalkShowTexts` VALUES (318350, 5, 1, '它售价一百十八元。', 'It costs one hundred and eighteen yuan.', NULL, NULL, '16.5', '21.6');
INSERT INTO `TalkShowTexts` VALUES (318350, 9, 1, '它很便宜。', 'It’s cheap.', NULL, NULL, '34.6', '36.1');
INSERT INTO `TalkShowTexts` VALUES (318353, 1, 1, '从前有个小男孩。', 'Once upon a time, there was a boy.', NULL, NULL, '1.0', '5.1');
INSERT INTO `TalkShowTexts` VALUES (318353, 2, 1, '他每天都在放羊，感觉无聊。', 'He looked after sheep everyday. The boy was bored.', NULL, NULL, '5.4', '12.1');
INSERT INTO `TalkShowTexts` VALUES (318353, 4, 1, '每个人都往山上跑。可是没看到狼。', 'Everyone ran up the hill. There was no wolf.', NULL, NULL, '22.4', '28.4');
INSERT INTO `TalkShowTexts` VALUES (318353, 5, 1, '小男孩笑了。', 'The boy laughed.', NULL, NULL, '28.8', '31.2');
INSERT INTO `TalkShowTexts` VALUES (318353, 6, 1, '第二天，小男孩又跑到村庄里。', 'The next day, the boy ran to the village again. ', NULL, NULL, '32.0', '36.4');
INSERT INTO `TalkShowTexts` VALUES (318353, 7, 1, '喊道：“有狼！有狼！”', 'Wolf! Wolf! He shouted.', NULL, NULL, '36.8', '41.1');
INSERT INTO `TalkShowTexts` VALUES (318353, 12, 1, '他们说道：“不可以说谎！”', 'They said, \"Don’t tell lies!\"', NULL, NULL, '63.8', '68.1');
INSERT INTO `TalkShowTexts` VALUES (318353, 13, 1, '狼把羊都吃掉了。', 'The wolf ate all the sheep.', NULL, NULL, '68.7', '71.9');
INSERT INTO `TalkShowTexts` VALUES (318353, 14, 1, '男孩得到了教训，再也不撒谎了。', 'The boy learned a lesson and wouldn’t tell lies again.', NULL, NULL, '72.4', '78.0');
INSERT INTO `TalkShowTexts` VALUES (318355, 2, 1, '上个月的时候，她来到一家商店。', 'Last month, she went into a shop.', NULL, NULL, '3.4', '6.8');
INSERT INTO `TalkShowTexts` VALUES (318355, 3, 1, '她买了一些奶酪，然后吃掉了这些奶酪。', 'She bought some cheese, and she ate the cheese.', NULL, NULL, '7.1', '12.0');
INSERT INTO `TalkShowTexts` VALUES (318355, 5, 1, '她非常害怕，跑开了。', 'She was scared and ran away.', NULL, NULL, '16.7', '20.1');
INSERT INTO `TalkShowTexts` VALUES (318355, 6, 1, '这只猫喊道：“喵，欢迎再来我的商店。喵。”', 'The cat shouted, \"Meow. Come to my shop again. Meow.\"', NULL, NULL, '20.5', '28.8');
INSERT INTO `TalkShowTexts` VALUES (318358, 8, 1, '琵琶看起来像吉他？', 'It looks like a guitar.', NULL, NULL, '26.0', '28.5');
INSERT INTO `TalkShowTexts` VALUES (318360, 1, 1, '去年，我参加了一次音乐会表演。', 'I played in a concert last year.', NULL, NULL, '0.8', '4.2');
INSERT INTO `TalkShowTexts` VALUES (318360, 3, 1, '我在表演前非常紧张。', 'I was very nervous before the concert.', NULL, NULL, '7.5', '11.6');
INSERT INTO `TalkShowTexts` VALUES (318360, 4, 1, '最后，观众都在鼓掌。', 'In the end, everyone clapped.', NULL, NULL, '12.3', '15.7');
INSERT INTO `TalkShowTexts` VALUES (318360, 6, 1, '我也感到非常骄傲。', 'And I was very proud.', NULL, NULL, '17.9', '20.5');
INSERT INTO `TalkShowTexts` VALUES (318361, 7, 1, '小呀小呀小提琴。', 'Vio - vio - violin, violin, violin.', NULL, NULL, '20.3', '25.2');
INSERT INTO `TalkShowTexts` VALUES (318363, 4, 1, '汤姆在唱歌。你能听到吗？', 'Tom is singing. Can you hear?', NULL, NULL, '26.5', '29.9');
INSERT INTO `TalkShowTexts` VALUES (318363, 10, 1, '这是为我们的妈妈准备的一个惊喜派对。', 'It’s a surprise party for our mothers.', NULL, NULL, '52.2', '56.0');
INSERT INTO `TalkShowTexts` VALUES (318365, 5, 1, '他还在上面留了言。', 'He’s writing a message to his father, too.', NULL, NULL, '17.9', '21.7');
INSERT INTO `TalkShowTexts` VALUES (318366, 2, 1, '在这特殊的一天，你会绘画吗？', 'Are you painting a picture on this special day?', NULL, NULL, '12.1', '16.0');
INSERT INTO `TalkShowTexts` VALUES (318366, 4, 1, '让你爸爸在父亲节高兴。', 'Make your father happy on Father’s Day.', NULL, NULL, '20.2', '24.1');
INSERT INTO `TalkShowTexts` VALUES (318368, 10, 1, '哇！我们的英语报纸真是太棒了！', 'Wow! Our English newspaper is fantastic!', NULL, NULL, '42.5', '48.7');
INSERT INTO `TalkShowTexts` VALUES (318369, 2, 1, '我们来画画吧！', 'Let’s paint a picture!', NULL, NULL, '6.6', '9.1');
INSERT INTO `TalkShowTexts` VALUES (318370, 1, 1, '星期六：玩耍日！', 'SATURDAY: FUN DAY!', NULL, NULL, '0.8', '4.7');
INSERT INTO `TalkShowTexts` VALUES (318370, 5, 1, '我们在那儿见面吧！', 'Let’s meet there!', NULL, NULL, '14.4', '16.7');
INSERT INTO `TalkShowTexts` VALUES (318370, 6, 1, '我们可以在公园东边踢足球。', 'We can play football in the east of the park.', NULL, NULL, '17.0', '21.3');
INSERT INTO `TalkShowTexts` VALUES (318370, 7, 1, '我们还可以在公园西边放风筝。', 'And we can fly our kites in the west of the park.', NULL, NULL, '21.6', '26.4');
INSERT INTO `TalkShowTexts` VALUES (318370, 8, 1, '我们十点在公园集合吧。', 'We will be there at 10 o’clock.', NULL, NULL, '26.8', '29.7');
INSERT INTO `TalkShowTexts` VALUES (318371, 2, 1, '真是个好主意！', 'That’s a great idea!', NULL, NULL, '7.5', '9.4');
INSERT INTO `TalkShowTexts` VALUES (318371, 4, 1, '让我们一起来写报告！', 'Let’s write the reports!', NULL, NULL, '12.4', '14.7');
INSERT INTO `TalkShowTexts` VALUES (318371, 6, 1, '让我们把它们粘起来！', 'Let’s stick them all together!', NULL, NULL, '17.1', '19.5');
INSERT INTO `TalkShowTexts` VALUES (318373, 1, 1, '山姆，中国动画片《少林小子》晚上8点开始。', 'Sam, the Chinese cartoon Shaolin Kids is on TV at 8 o’clock.', NULL, NULL, '1.2', '9.5');
INSERT INTO `TalkShowTexts` VALUES (318373, 2, 1, '耶！我可喜欢看了。', 'Hooray! I like it very much.', NULL, NULL, '9.6', '14.3');
INSERT INTO `TalkShowTexts` VALUES (318375, 1, 1, '伦敦时间现在是11点30。', 'It’s 11:30 am in London.', NULL, NULL, '0.6', '4.8');
INSERT INTO `TalkShowTexts` VALUES (318375, 3, 1, '北京时间现在是7点30。', 'It’s 7:30 pm in Beijing.', NULL, NULL, '7.3', '11.2');
INSERT INTO `TalkShowTexts` VALUES (318376, 2, 1, '表上什么时间了？', 'What’s the time on the clock?', NULL, NULL, '8.0', '10.4');
INSERT INTO `TalkShowTexts` VALUES (318377, 1, 1, '金窝银窝不如自己的草窝。', 'East and west, home is the best.', NULL, NULL, '5.1', '9.5');
INSERT INTO `TalkShowTexts` VALUES (318377, 2, 1, '从南到北，让我们歇息一下。', 'North and south, let’s have a rest.', NULL, NULL, '9.7', '14.5');
INSERT INTO `TalkShowTexts` VALUES (318378, 1, 1, '看！这个国家是美国。', 'Look! This country is the US.', NULL, NULL, '3.2', '8.0');
INSERT INTO `TalkShowTexts` VALUES (318378, 2, 1, '人们在美国说英语。', 'People in the US speak English.', NULL, NULL, '8.4', '12.3');
INSERT INTO `TalkShowTexts` VALUES (318378, 11, 1, '纽约是美国首都吗？', 'Is New York the capital of the US?', NULL, NULL, '42.7', '46.9');
INSERT INTO `TalkShowTexts` VALUES (318378, 14, 1, '也许你可以拜访你的表哥。', 'Maybe you can visit your cousin.', NULL, NULL, '59.5', '62.3');
INSERT INTO `TalkShowTexts` VALUES (318380, 1, 1, '这是中国地图。', 'This is a map of China.', NULL, NULL, '0.4', '3.2');
INSERT INTO `TalkShowTexts` VALUES (318383, 3, 1, '他们讲什么语言呢？', 'What language do they speak there?', NULL, NULL, '11.9', '15.2');
INSERT INTO `TalkShowTexts` VALUES (318383, 6, 1, '因为我喜欢那里的动物。', 'Because I love Australian animals.', NULL, NULL, '21.8', '25.5');
INSERT INTO `TalkShowTexts` VALUES (318383, 7, 1, '我喜欢袋鼠。', 'I love kangaroos.', NULL, NULL, '26.3', '28.7');
INSERT INTO `TalkShowTexts` VALUES (318385, 2, 1, '他们靠后腿弹跳行走。', 'They jump on their back legs.', NULL, NULL, '4.0', '6.9');
INSERT INTO `TalkShowTexts` VALUES (318386, 5, 1, '因为我的袋鼠风筝飞走了。', 'Because I lost my kangaroo kite.', NULL, NULL, '39.9', '44.1');
INSERT INTO `TalkShowTexts` VALUES (318388, 7, 1, '我现在还没有计划好。', 'I haven’t got any plan now.', NULL, NULL, '25.0', '28.4');
INSERT INTO `TalkShowTexts` VALUES (318390, 4, 1, '去看大本钟，参观一些博物馆。', 'I’ll see Big Ben and some museums.', NULL, NULL, '10.6', '14.9');
INSERT INTO `TalkShowTexts` VALUES (318390, 5, 1, '我们会去英国餐厅吃饭。', 'We’re going to eat in an English restaurant.', NULL, NULL, '15.3', '19.0');
INSERT INTO `TalkShowTexts` VALUES (318391, 6, 1, '不管你在哪儿，我都会送上我的明信片。', 'I will send a postcard to wherever you are.', NULL, NULL, '25.1', '30.8');
INSERT INTO `TalkShowTexts` VALUES (318393, 2, 1, '你在中国两年了。', 'You were in China for two years.', NULL, NULL, '7.0', '10.3');
INSERT INTO `TalkShowTexts` VALUES (318393, 3, 1, '伦敦现在看起来不一样了吗？', 'Does London look different now?', NULL, NULL, '10.7', '13.0');
INSERT INTO `TalkShowTexts` VALUES (318393, 6, 1, '这里以前没有长椅。', 'There weren’t any benches here before.', NULL, NULL, '17.8', '20.7');
INSERT INTO `TalkShowTexts` VALUES (318393, 15, 1, '那我们来喂它们吧。', 'Then let’s feed them.', NULL, NULL, '44.6', '47.0');
INSERT INTO `TalkShowTexts` VALUES (318395, 5, 1, '现在是一个大操场了。', 'There is a big playground now.', NULL, NULL, '15.0', '17.7');
INSERT INTO `TalkShowTexts` VALUES (318398, 3, 1, '太好了！我们走吧！', 'Great! Let’s go!', NULL, NULL, '9.2', '11.8');
INSERT INTO `TalkShowTexts` VALUES (318398, 9, 1, '这袋好重啊。', 'This one is heavy.', NULL, NULL, '41.2', '43.4');
INSERT INTO `TalkShowTexts` VALUES (318400, 5, 1, '有时候超市里有餐厅。', 'Sometimes there are restaurants at the supermarkets.', NULL, NULL, '21.3', '26.0');
INSERT INTO `TalkShowTexts` VALUES (318401, 1, 1, '超市卖糖果。', 'Supermarkets sell sweets.', NULL, NULL, '5.0', '7.6');
INSERT INTO `TalkShowTexts` VALUES (318403, 1, 1, '玲玲，今天是万圣夜。', 'Lingling, today is Halloween.', NULL, NULL, '1.2', '5.0');
INSERT INTO `TalkShowTexts` VALUES (318403, 3, 1, '这是英国的一个秋天的节日。', 'It’s an autumn festival in the UK.', NULL, NULL, '7.5', '11.4');
INSERT INTO `TalkShowTexts` VALUES (318403, 7, 1, '是的。看看这些帽子和面具。', 'Yes. Look at the hats and the masks!', NULL, NULL, '20.8', '25.5');
INSERT INTO `TalkShowTexts` VALUES (318403, 8, 1, '他们会去邻居家并问道，不招待就使坏。', 'They go to their neighbours’ houses and ask, \"Trick or treat?\"', NULL, NULL, '31.2', '37.7');
INSERT INTO `TalkShowTexts` VALUES (318403, 9, 1, '然后他们的邻居就会给他们糖果。', 'Then their neighbours give them sweets.', NULL, NULL, '38.1', '41.7');
INSERT INTO `TalkShowTexts` VALUES (318403, 10, 1, '那晚……', 'That night...', NULL, NULL, '42.4', '44.1');
INSERT INTO `TalkShowTexts` VALUES (318403, 12, 1, '你们看起来好吓人啊！', 'You look so scary!', NULL, NULL, '47.5', '50.0');
INSERT INTO `TalkShowTexts` VALUES (318405, 2, 1, '帽子上有玩具小鸡和花。', 'There are toy chicks and flowers on the hats. ', NULL, NULL, '4.3', '8.3');
INSERT INTO `TalkShowTexts` VALUES (318405, 4, 1, '它们是复活节帽子。', 'They are Easter hats.', NULL, NULL, '10.8', '13.1');
INSERT INTO `TalkShowTexts` VALUES (318405, 6, 1, '人们给孩子巧克力蛋。', 'People give children chocolate eggs. ', NULL, NULL, '17.9', '21.2');
INSERT INTO `TalkShowTexts` VALUES (318408, 2, 1, '这是我生日聚会的请柬。', 'It’s an invitation to my birthday party.', NULL, NULL, '2.0', '6.1');
INSERT INTO `TalkShowTexts` VALUES (318408, 9, 1, '吃完饭，我们会去讲故事。', 'After lunch, we’re going to tell stories.', NULL, NULL, '28.3', '32.5');
INSERT INTO `TalkShowTexts` VALUES (318408, 11, 1, '但在那之前，你们要帮我打扫屋子！', 'But before that, you are going to help me tidy up!', NULL, NULL, '37.2', '42.9');
INSERT INTO `TalkShowTexts` VALUES (318410, 4, 1, '日期：11月2日，星期日。', 'On: Sunday, 2nd November ', NULL, NULL, '14.6', '18.6');
INSERT INTO `TalkShowTexts` VALUES (318410, 5, 1, '开始时间：上午11点。', 'From: 11:00 AM ', NULL, NULL, '19.1', '21.8');
INSERT INTO `TalkShowTexts` VALUES (318410, 6, 1, '结束时间：下午2点。', 'To: 2:00 PM ', NULL, NULL, '22.0', '24.5');
INSERT INTO `TalkShowTexts` VALUES (318410, 7, 1, '你能来吗？请答复。', 'Can you come? Please reply. ', NULL, NULL, '24.9', '28.0');
INSERT INTO `TalkShowTexts` VALUES (318413, 1, 1, '这是我的！', 'It’s mine!', NULL, NULL, '1.6', '4.6');
INSERT INTO `TalkShowTexts` VALUES (318413, 2, 1, '这不是你的，山姆，是我的。', 'It isn’t yours, Sam. It’s mine!', NULL, NULL, '4.6', '9.6');
INSERT INTO `TalkShowTexts` VALUES (318413, 5, 1, '但那不是她的。是我的！', 'But it isn’t hers. It’s mine!', NULL, NULL, '17.1', '21.3');
INSERT INTO `TalkShowTexts` VALUES (318413, 7, 1, '但是你们两个的红T恤衫都在那儿。', 'But both your red T-shirts are there.', NULL, NULL, '28.0', '31.9');
INSERT INTO `TalkShowTexts` VALUES (318413, 8, 1, '都湿了。看！', 'They are wet. Look!', NULL, NULL, '32.1', '35.1');
INSERT INTO `TalkShowTexts` VALUES (318413, 9, 1, '那么这是谁的T恤衫？', 'Then whose T-shirt is it?', NULL, NULL, '35.4', '38.6');
INSERT INTO `TalkShowTexts` VALUES (318413, 13, 1, '好了，没事了。', 'That’s OK.', NULL, NULL, '50.4', '51.7');
INSERT INTO `TalkShowTexts` VALUES (318414, 4, 1, '我不知道。但是请不要摘走它们。', 'I don’t know. But don’t take them away.', NULL, NULL, '11.8', '15.3');
INSERT INTO `TalkShowTexts` VALUES (318415, 7, 1, '哦，但是工人们还在工作。', 'Oh, but the workers are still working.', NULL, NULL, '22.9', '27.5');
INSERT INTO `TalkShowTexts` VALUES (318418, 5, 1, '但是你能跳得很高啊！', 'But you can jump really high!', NULL, NULL, '15.9', '19.4');
INSERT INTO `TalkShowTexts` VALUES (318418, 6, 1, '那是真的！', 'That’s true!', NULL, NULL, '19.7', '21.1');
INSERT INTO `TalkShowTexts` VALUES (318418, 10, 1, '我们队得了八十分，你一个人就得了二十分。', 'Our team got eighty points and you got twenty points!', NULL, NULL, '37.5', '43.1');
INSERT INTO `TalkShowTexts` VALUES (318418, 11, 1, '玲玲，你打篮球可以打得很好的。', 'You can play basketball well, Lingling.', NULL, NULL, '43.5', '47.0');
INSERT INTO `TalkShowTexts` VALUES (318418, 12, 1, '我是你的第一位粉丝！', 'I’m your first fan!', NULL, NULL, '47.5', '49.8');
INSERT INTO `TalkShowTexts` VALUES (318420, 2, 1, '他们得了七十分。', 'They got seventy points.', NULL, NULL, '8.7', '11.3');
INSERT INTO `TalkShowTexts` VALUES (318420, 3, 1, '另一队得了六十四分。', 'The other team got sixty-four points.', NULL, NULL, '11.8', '15.6');
INSERT INTO `TalkShowTexts` VALUES (318422, 4, 1, '狗狗很有用。', 'Dogs are useful.', NULL, NULL, '14.6', '16.7');
INSERT INTO `TalkShowTexts` VALUES (318423, 1, 1, '看！有一个关于狗的电视节目。', 'Look! There’s a TV show about dogs.', NULL, NULL, '0.9', '5.5');
INSERT INTO `TalkShowTexts` VALUES (318423, 4, 1, '这个人是盲人。他的狗可以帮助他。', 'This man is blind. His dog can help him.', NULL, NULL, '20.7', '25.6');
INSERT INTO `TalkShowTexts` VALUES (318425, 7, 1, '那么消防员们就能够帮助他们了。', 'Then the firefighters could help them.', NULL, NULL, '22.5', '26.0');
INSERT INTO `TalkShowTexts` VALUES (318426, 11, 1, '我一句话也说不出来。', 'I can’t speak a word.', NULL, NULL, '39.0', '41.4');
INSERT INTO `TalkShowTexts` VALUES (318428, 5, 1, '在英国，孩子们上课时经常围坐在桌子旁。', 'In the UK, chlidren often sit around tables in class.', NULL, NULL, '20.4', '26.3');
INSERT INTO `TalkShowTexts` VALUES (318428, 6, 1, '在中国，我们则坐成一排。', 'We sit in lines in China.', NULL, NULL, '27.1', '30.1');
INSERT INTO `TalkShowTexts` VALUES (318428, 9, 1, '在英国，学校通常9点开始上课', 'In the UK, school often starts at 9 o’clock ', NULL, NULL, '39.7', '44.9');
INSERT INTO `TalkShowTexts` VALUES (318428, 10, 1, '下午三点半放学。', 'And finishes at half past three.', NULL, NULL, '45.1', '48.3');
INSERT INTO `TalkShowTexts` VALUES (318428, 14, 1, '但是我很想念我在中国的朋友们。', 'But I miss my friends back in China.', NULL, NULL, '61.6', '65.6');
INSERT INTO `TalkShowTexts` VALUES (318429, 3, 1, '我已经把书打开了。', 'My books are open now.', NULL, NULL, '9.6', '11.6');
INSERT INTO `TalkShowTexts` VALUES (318430, 2, 1, '墙上有孩子们的画。', 'There are children’s paintings on the walls.', NULL, NULL, '4.6', '8.2');
INSERT INTO `TalkShowTexts` VALUES (318430, 4, 1, '学生们休息时玩得很开心。', 'The students can have fun at break time.', NULL, NULL, '11.9', '15.7');
INSERT INTO `TalkShowTexts` VALUES (318430, 5, 1, '英国的孩子们学习很努力。', 'English children study hard.', NULL, NULL, '17.0', '20.1');
INSERT INTO `TalkShowTexts` VALUES (318430, 6, 1, '他们每天都做家庭作业。', 'They do homework every day.', NULL, NULL, '20.6', '23.5');
INSERT INTO `TalkShowTexts` VALUES (318433, 10, 1, '没事。我在思考。', 'Nothing. I’m thinking.', NULL, NULL, '33.2', '36.3');
INSERT INTO `TalkShowTexts` VALUES (318435, 7, 1, '所以她喝了一些水。', 'So she drank some water.', NULL, NULL, '27.7', '30.2');
INSERT INTO `TalkShowTexts` VALUES (318437, 1, 1, 'Sam，你应该9点钟上床睡觉。', 'Sam, you should go to bed at 9.', NULL, NULL, '1.3', '5.7');
INSERT INTO `TalkShowTexts` VALUES (318438, 9, 1, '哇！这个房间现在真干净。', 'Wow! The room is clean now.', NULL, NULL, '40.4', '45.2');
INSERT INTO `TalkShowTexts` VALUES (318440, 1, 1, '班级规则', 'Class Rules', NULL, NULL, '2.3', '4.2');
INSERT INTO `TalkShowTexts` VALUES (318440, 3, 1, '你应该把你的书带到班里。', 'You should bring your books to class.', NULL, NULL, '8.6', '12.0');
INSERT INTO `TalkShowTexts` VALUES (318440, 6, 1, '你应该友善。', 'You should be kind.', NULL, NULL, '20.6', '22.7');
INSERT INTO `TalkShowTexts` VALUES (318441, 1, 1, '你不应该在床上跳，我姐姐说。', 'You shouldn’t jump on the bed, my big sister said.', NULL, NULL, '5.1', '10.1');
INSERT INTO `TalkShowTexts` VALUES (318443, 5, 1, '她开什么车？', 'What did she drive?', NULL, NULL, '17.3', '19.6');
INSERT INTO `TalkShowTexts` VALUES (318443, 6, 1, '她开公共汽车。', 'She drove a bus.', NULL, NULL, '19.7', '21.7');
INSERT INTO `TalkShowTexts` VALUES (318443, 9, 1, '他以前是一位吹笛手。', 'He was a flute player before.', NULL, NULL, '26.9', '29.5');
INSERT INTO `TalkShowTexts` VALUES (318444, 2, 1, '他在钓鱼。', 'He is fishing.', NULL, NULL, '2.8', '4.3');
INSERT INTO `TalkShowTexts` VALUES (318445, 4, 1, '他在一间办公室工作。', 'He worked in an office.', NULL, NULL, '9.8', '12.6');
INSERT INTO `TalkShowTexts` VALUES (318448, 6, 1, '她吃了鸡蛋和香肠。', 'She had eggs and sausages.', NULL, NULL, '18.1', '21.7');
INSERT INTO `TalkShowTexts` VALUES (318448, 8, 1, '她吃了三明治。', 'She had sandwiches.', NULL, NULL, '25.2', '27.8');
INSERT INTO `TalkShowTexts` VALUES (318448, 10, 1, '她吃了鱼和薯条。', 'She had fish and chips.', NULL, NULL, '30.7', '34.2');
INSERT INTO `TalkShowTexts` VALUES (318448, 13, 1, '那是传统的晚餐。', 'It’s traditonal dinner.', NULL, NULL, '38.0', '41.2');
INSERT INTO `TalkShowTexts` VALUES (318448, 16, 1, '她说它们很好吃。', 'She says it’s dilicious.', NULL, NULL, '46.9', '49.8');
INSERT INTO `TalkShowTexts` VALUES (318450, 6, 1, '他们吃鸡肉，土豆和蔬菜。', 'They eat chicken, potatoes and other vegetables.', NULL, NULL, '26.5', '32.1');
INSERT INTO `TalkShowTexts` VALUES (318453, 3, 1, '请问有哈利·波特的影碟吗？', 'Have you got the Harry Potter DVDs?', NULL, NULL, '6.4', '9.7');
INSERT INTO `TalkShowTexts` VALUES (318453, 9, 1, '我带你去。', 'I’ll show them to you.', NULL, NULL, '24.9', '26.8');
INSERT INTO `TalkShowTexts` VALUES (318454, 2, 1, '你可以从这里借书。', 'You can borrow books from here.', NULL, NULL, '3.0', '5.8');
INSERT INTO `TalkShowTexts` VALUES (318457, 1, 1, '我给你寄了一辆自行车。', 'I sent you a bike.', NULL, NULL, '4.3', '6.2');
INSERT INTO `TalkShowTexts` VALUES (318458, 11, 1, '我还寄了一些美国巧克力给你。', 'And I sent you American chocolate.', NULL, NULL, '29.3', '33.0');
INSERT INTO `TalkShowTexts` VALUES (318458, 17, 1, '你都看了吗？', 'Did you read them?', NULL, NULL, '46.0', '47.8');
INSERT INTO `TalkShowTexts` VALUES (318458, 20, 1, '它们很有趣。', 'They are interesting.', NULL, NULL, '51.9', '54.1');
INSERT INTO `TalkShowTexts` VALUES (318460, 1, 1, '你最喜欢哪个季节？', 'What’s your favourite season?', NULL, NULL, '1.0', '3.4');
INSERT INTO `TalkShowTexts` VALUES (318460, 16, 1, '但我可以玩雪。', 'I can play with the snow.', NULL, NULL, '41.6', '43.3');
INSERT INTO `TalkShowTexts` VALUES (318462, 1, 1, '那个书包多少钱?', 'How much is that bag?', NULL, NULL, '0.2', '2.8');
INSERT INTO `TalkShowTexts` VALUES (318463, 10, 1, '它很轻。', 'It’s light.', NULL, NULL, '28.4', '30.1');
INSERT INTO `TalkShowTexts` VALUES (318463, 14, 1, '她携带比较轻便。', 'It will be easy for her to carry.', NULL, NULL, '40.9', '43.8');
INSERT INTO `TalkShowTexts` VALUES (318463, 16, 1, '三十磅。', 'It’s 30 pounds.', NULL, NULL, '46.3', '48.5');
INSERT INTO `TalkShowTexts` VALUES (318464, 1, 1, '你可以把我的书包背起来吗?', 'Can you carry my bag on your back?', NULL, NULL, '1.2', '4.5');
INSERT INTO `TalkShowTexts` VALUES (318465, 4, 1, '我在肩上挎着。', 'I carry it over my shoulder.', NULL, NULL, '10.9', '13.4');
INSERT INTO `TalkShowTexts` VALUES (318467, 2, 1, '建造他们是为了什么?', 'And what did they build it for?', NULL, NULL, '7.4', '9.8');
INSERT INTO `TalkShowTexts` VALUES (318468, 4, 1, '那是一个非常古老的地方。', 'It’s a very old place.', NULL, NULL, '8.6', '11.0');
INSERT INTO `TalkShowTexts` VALUES (318468, 5, 1, '巨石阵大约有五千年的历史了。', 'It’s about five thousand years old.', NULL, NULL, '11.4', '14.8');
INSERT INTO `TalkShowTexts` VALUES (318468, 9, 1, '石头围成一圈。', 'They are in a circle.', NULL, NULL, '24.2', '25.9');
INSERT INTO `TalkShowTexts` VALUES (318468, 15, 1, '也许你会找到答案！', 'Maybe you will find the answer!', NULL, NULL, '41.1', '43.2');
INSERT INTO `TalkShowTexts` VALUES (318468, 16, 1, '希望如此！', 'I hope so!', NULL, NULL, '43.7', '45.2');
INSERT INTO `TalkShowTexts` VALUES (318468, 17, 1, '我们怎么去那里呢？', 'How will we get there?', NULL, NULL, '46.6', '48.3');
INSERT INTO `TalkShowTexts` VALUES (318468, 19, 1, '到那里需要三个小时。', 'It will take three hours.', NULL, NULL, '50.6', '52.9');
INSERT INTO `TalkShowTexts` VALUES (318469, 1, 1, '大明 ，过来乘自行车。', 'Daming, come and take a bike ride.', NULL, NULL, '0.1', '4.2');
INSERT INTO `TalkShowTexts` VALUES (318470, 5, 1, '在巨石阵上方   我们坐了一会儿直升飞机!', 'We took a short helicopter ride over Stonehenge!', NULL, NULL, '13.6', '18.8');
INSERT INTO `TalkShowTexts` VALUES (318470, 8, 1, '真的很棒。', 'It was amazing.', NULL, NULL, '26.9', '29.1');
INSERT INTO `TalkShowTexts` VALUES (318473, 4, 1, '我的爸爸每天晚上六点去工作。', 'My father goes to work at 6 o’clock every evening.', NULL, NULL, '11.7', '16.8');
INSERT INTO `TalkShowTexts` VALUES (318473, 7, 1, '他是演员。', 'He’s an actor.', NULL, NULL, '21.2', '22.7');
INSERT INTO `TalkShowTexts` VALUES (318473, 8, 1, '他很忙。', 'He’s very busy.', NULL, NULL, '22.9', '24.5');
INSERT INTO `TalkShowTexts` VALUES (318474, 1, 1, '哦,不!   现在七点四十五分了。', 'Oh no! It’s a quarter to eight now.', NULL, NULL, '5.4', '9.4');
INSERT INTO `TalkShowTexts` VALUES (318478, 6, 1, '他妈妈是中国人。', 'His mother is Chinese.', NULL, NULL, '18.9', '21.4');
INSERT INTO `TalkShowTexts` VALUES (318479, 1, 1, '我画了一条龙。', 'I drew a dragon.', NULL, NULL, '5.2', '7.2');
INSERT INTO `TalkShowTexts` VALUES (318480, 2, 1, '我在一张黄色的纸上画了一条龙。', 'I drew a dragon on a piece of yellow paper.', NULL, NULL, '6.1', '10.2');
INSERT INTO `TalkShowTexts` VALUES (318480, 3, 1, '我给它涂上颜色。', 'I painted it.', NULL, NULL, '10.9', '12.9');
INSERT INTO `TalkShowTexts` VALUES (318480, 4, 1, '我用剪刀剪下来。', 'I cut the paper with scissors.', NULL, NULL, '13.6', '16.6');
INSERT INTO `TalkShowTexts` VALUES (318480, 5, 1, '支上竹条。', 'I put sticks on it.', NULL, NULL, '17.4', '19.7');
INSERT INTO `TalkShowTexts` VALUES (318480, 6, 1, '系上绳子。', 'I tied strings to it.', NULL, NULL, '20.6', '23.1');
INSERT INTO `TalkShowTexts` VALUES (318483, 7, 1, '上星期我们去了儿童剧院。', 'Last week we went to a children’s theatre.', NULL, NULL, '24.9', '28.8');
INSERT INTO `TalkShowTexts` VALUES (318483, 8, 1, '男人们穿着女人的衣服。', 'The men wore women’s clothes.', NULL, NULL, '29.6', '32.4');
INSERT INTO `TalkShowTexts` VALUES (318483, 10, 1, '演员们讲了很多笑话。', 'The actors told lots of jokes.', NULL, NULL, '36.1', '39.0');
INSERT INTO `TalkShowTexts` VALUES (318483, 11, 1, '真有趣。', 'It was very funny.', NULL, NULL, '39.5', '41.4');
INSERT INTO `TalkShowTexts` VALUES (318483, 14, 1, '表演过后我们去了一家餐馆。', 'After the show we went to a restaurant.', NULL, NULL, '48.9', '52.2');
INSERT INTO `TalkShowTexts` VALUES (318485, 4, 1, '爸爸在我的房间又放了一张床。', 'Dad put another bed in my room.', NULL, NULL, '10.6', '14.0');
INSERT INTO `TalkShowTexts` VALUES (318485, 7, 1, '爸爸在读一本关于中国历史的书。', 'Dad read a book about Chinese history.', NULL, NULL, '23.7', '27.0');
INSERT INTO `TalkShowTexts` VALUES (318485, 10, 1, '历史是我最喜欢的学科。', 'History is my favourite subject.', NULL, NULL, '33.2', '36.3');
INSERT INTO `TalkShowTexts` VALUES (318488, 7, 1, '我的机票，还有护照。', 'my ticket and my passport.', NULL, NULL, '23.2', '27.9');
INSERT INTO `TalkShowTexts` VALUES (318488, 11, 1, '你明天什么时候走啊？', 'When will you leave tomorrow?', NULL, NULL, '43.3', '45.5');
INSERT INTO `TalkShowTexts` VALUES (318490, 3, 1, '我是七月去的。', 'I went there in July.', NULL, NULL, '7.0', '8.8');
INSERT INTO `TalkShowTexts` VALUES (318490, 11, 1, '我是八月去的。', 'I went there in August.', NULL, NULL, '31.2', '33.2');
INSERT INTO `TalkShowTexts` VALUES (318490, 13, 1, '我在那见到了我的同学们。', 'I met my classmates there.', NULL, NULL, '38.2', '40.4');
INSERT INTO `TalkShowTexts` VALUES (318493, 7, 1, '它离我家不远。', 'It’s near my home.', NULL, NULL, '19.3', '21.2');
INSERT INTO `TalkShowTexts` VALUES (318493, 8, 1, '我们能走完长城的全程吗？', 'Can we walk along all of the Great Wall?', NULL, NULL, '21.3', '24.7');
INSERT INTO `TalkShowTexts` VALUES (318493, 11, 1, '长城有多长？', 'How long is the Great Wall?', NULL, NULL, '30.9', '33.4');
INSERT INTO `TalkShowTexts` VALUES (318493, 12, 1, '它有四万多里长。', 'It’s more than forty thousand li long.', NULL, NULL, '33.6', '38.0');
INSERT INTO `TalkShowTexts` VALUES (318493, 14, 1, '是的，就是两万多公里。', 'Yes. That’s more than twenty thousand kilometres!', NULL, NULL, '40.5', '45.9');
INSERT INTO `TalkShowTexts` VALUES (318494, 2, 1, '它有三十米高。', 'It’s thirty meters tall.', NULL, NULL, '5.3', '9.1');
INSERT INTO `TalkShowTexts` VALUES (318495, 4, 1, '它是一栋写字楼。', 'It’s an office building.', NULL, NULL, '11.7', '14.0');
INSERT INTO `TalkShowTexts` VALUES (318497, 1, 1, '纽约有个唐人街。', 'There is a Chinatown in New York.', NULL, NULL, '1.7', '5.0');
INSERT INTO `TalkShowTexts` VALUES (318497, 4, 1, '噢，中国所有的街道都是唐人街。', 'Oh, all towns in China are Chinatowns.', NULL, NULL, '11.7', '16.5');
INSERT INTO `TalkShowTexts` VALUES (318498, 3, 1, '主题：纽约唐人街', 'SUBJECT: Chinatown, New York', NULL, NULL, '13.6', '18.1');
INSERT INTO `TalkShowTexts` VALUES (318498, 7, 1, '那儿任何地方都有中国人。', 'There were Chinese people everywhere.', NULL, NULL, '29.1', '32.9');
INSERT INTO `TalkShowTexts` VALUES (318498, 9, 1, '人们说中文和英语。', 'The people spoke Chinese and English.', NULL, NULL, '39.1', '44.0');
INSERT INTO `TalkShowTexts` VALUES (318498, 12, 1, '然后我们看见街上有舞狮。', 'Then we saw a lion dance in the street.', NULL, NULL, '54.5', '58.8');
INSERT INTO `TalkShowTexts` VALUES (318500, 2, 1, '我们昨天去了明十三陵。', 'We went to the Ming Tombs yesterday.', NULL, NULL, '1.7', '4.9');
INSERT INTO `TalkShowTexts` VALUES (318500, 12, 1, '昨天我在英语课上写了一个故事。', 'I wrote a story in English class yesterday.', NULL, NULL, '38.3', '42.0');
INSERT INTO `TalkShowTexts` VALUES (318500, 14, 1, '我长大了想写一本书。', 'I want to write a book when I am big.', NULL, NULL, '46.0', '49.1');
INSERT INTO `TalkShowTexts` VALUES (318503, 2, 1, '我正在把我的新邮票放进集邮册里。', 'I’m putting my new stamps into my stamp book.', NULL, NULL, '7.2', '11.3');
INSERT INTO `TalkShowTexts` VALUES (318504, 3, 1, '哦，这是一张我那来自月亮的朋友的图片！', 'Oh, it’s a picture of my friend from the moon!', NULL, NULL, '14.1', '20.0');
INSERT INTO `TalkShowTexts` VALUES (318505, 1, 1, '集邮是我的爱好。', 'Collecting stamps is my hobby.', NULL, NULL, '0.0', '3.5');
INSERT INTO `TalkShowTexts` VALUES (318505, 3, 1, '这是海南岛的一张照片，是中国我最喜欢的地方。', 'It’s a picture of Hainan Island, my favourite place in China.', NULL, NULL, '11.2', '18.9');
INSERT INTO `TalkShowTexts` VALUES (318505, 4, 1, '我们可以看到五指山和一棵椰子树。', 'We can see the Five-Finger Mountain and a coconut tree.', NULL, NULL, '19.4', '25.7');
INSERT INTO `TalkShowTexts` VALUES (318507, 2, 1, '感恩节是他们最喜欢的节日。', 'Thanksgiving is their favourite day.', NULL, NULL, '6.1', '10.1');
INSERT INTO `TalkShowTexts` VALUES (318508, 11, 1, '感恩节快到了。', 'Thanksgiving is nearly here.', NULL, NULL, '42.2', '45.6');
INSERT INTO `TalkShowTexts` VALUES (318509, 1, 1, '在春节，我们吃许多食物。', 'At the Spring Festival, we eat lots of food.', NULL, NULL, '2.1', '6.7');
INSERT INTO `TalkShowTexts` VALUES (318510, 2, 1, '当然。它在许多国家是一个非常重要的节日。', 'Sure. It’s a very important festival in many counties. ', NULL, NULL, '5.5', '11.2');
INSERT INTO `TalkShowTexts` VALUES (318510, 3, 1, '它在12月25日。', 'It is on the 25th of December.', NULL, NULL, '11.6', '15.2');
INSERT INTO `TalkShowTexts` VALUES (318510, 5, 1, '街上和商店里都有彩灯。', 'There are lights in the streets and the shops.', NULL, NULL, '19.0', '23.3');
INSERT INTO `TalkShowTexts` VALUES (318513, 4, 1, '很高兴见到你。', 'Pleased to meet you.', NULL, NULL, '11.9', '13.7');
INSERT INTO `TalkShowTexts` VALUES (318513, 19, 1, '哦，她很漂亮！', 'Oh, she’s pretty!', NULL, NULL, '51.6', '53.7');
INSERT INTO `TalkShowTexts` VALUES (318514, 4, 1, '我们可以用法语写信。', 'We can write in French.', NULL, NULL, '14.1', '16.8');
INSERT INTO `TalkShowTexts` VALUES (318514, 6, 1, '不。他想要一个电话朋友。哈哈 ……', 'no . he wants a phone friend. Ha ha...', NULL, NULL, '23.2', '28.0');
INSERT INTO `TalkShowTexts` VALUES (318517, 6, 1, '我有一本关于世界的书。', 'I’ve got a book about the world.', NULL, NULL, '17.6', '21.4');
INSERT INTO `TalkShowTexts` VALUES (318518, 11, 1, '但是对我来说使用它们很困难。', 'But it is very difficult for me to use them.', NULL, NULL, '33.4', '37.1');
INSERT INTO `TalkShowTexts` VALUES (318518, 17, 1, '请给我回信吧，我会回答你关于美国的问题的。', 'Please write back and I will answer your questions about the US.', NULL, NULL, '55.1', '60.3');
INSERT INTO `TalkShowTexts` VALUES (318520, 6, 1, '我的美术老师林老师，', 'Miss Lin, my Art teacher,', NULL, NULL, '19.3', '22.1');
INSERT INTO `TalkShowTexts` VALUES (318523, 3, 1, '赛门把它给我了。', 'Simon gave it to me.', NULL, NULL, '11.9', '14.3');
INSERT INTO `TalkShowTexts` VALUES (318523, 7, 1, '熊猫爱竹子。', 'Pandas love bamboo.', NULL, NULL, '24.0', '26.8');
INSERT INTO `TalkShowTexts` VALUES (318523, 11, 1, '那条蛇在用它的身体跳舞。', 'The snake can use its body to dance.', NULL, NULL, '42.0', '46.2');
INSERT INTO `TalkShowTexts` VALUES (318523, 13, 1, '不，它们不喜欢。他们几乎是聋的。', 'No,they don’t.They are almost deaf.', NULL, NULL, '51.1', '55.4');
INSERT INTO `TalkShowTexts` VALUES (318523, 16, 1, '所以它害怕了。', 'So it gets frightened.', NULL, NULL, '63.7', '66.0');
INSERT INTO `TalkShowTexts` VALUES (318525, 3, 1, '一只狐狸在白天睡觉，晚上不睡觉。', 'A fox sleeps in the day, and doesn’t sleep at night.', NULL, NULL, '10.6', '16.4');
INSERT INTO `TalkShowTexts` VALUES (318527, 1, 1, '我整理了我的房间，发现了一枚硬币。', 'I tidied my room and I found a coin.', NULL, NULL, '1.9', '6.1');
INSERT INTO `TalkShowTexts` VALUES (318528, 5, 1, '他曾经整理过他的床吗？', 'Does he ever tidy his bed?', NULL, NULL, '14.8', '17.5');
INSERT INTO `TalkShowTexts` VALUES (318528, 10, 1, '没有整理。桌子很乱。', 'No, I don’t. It’s very messy.', NULL, NULL, '34.5', '38.6');
INSERT INTO `TalkShowTexts` VALUES (318529, 2, 1, '从来没有离开过。', 'I never go.', NULL, NULL, '7.4', '9.5');
INSERT INTO `TalkShowTexts` VALUES (318530, 3, 1, '我有时候会为我的老师们擦黑板。', 'I sometimes clean the blackboard for my teachers.', NULL, NULL, '6.0', '10.8');
INSERT INTO `TalkShowTexts` VALUES (318533, 5, 1, '联合国大厦想要让世界变得和平。', 'The UN wants to make peace in the world.', NULL, NULL, '16.1', '20.3');
INSERT INTO `TalkShowTexts` VALUES (318533, 8, 1, '是的。中国是联合国193个成员国之一。', 'Yes. China is one of the 193 member states in the UN.', NULL, NULL, '27.2', '35.4');
INSERT INTO `TalkShowTexts` VALUES (318535, 3, 1, '上海很大很有名。', 'Shanghai is very big and famous.', NULL, NULL, '8.8', '13.7');
INSERT INTO `TalkShowTexts` VALUES (318538, 1, 1, '这是个很漂亮的公园，简阿姨。', 'This is a beautiful park,Aunt Jane.', NULL, NULL, '1.0', '4.7');
INSERT INTO `TalkShowTexts` VALUES (318538, 11, 1, '我忘记了。', 'I forgot.', NULL, NULL, '28.4', '30.0');
INSERT INTO `TalkShowTexts` VALUES (318538, 18, 1, '这么喝水真有意思。', 'It’s fun to drink this way.', NULL, NULL, '54.3', '57.2');
INSERT INTO `TalkShowTexts` VALUES (318539, 2, 1, '请不要穿马路。', 'Please don’t cross.', NULL, NULL, '4.5', '7.1');
INSERT INTO `TalkShowTexts` VALUES (318542, 2, 1, '我想要一个热狗.', 'I want a hot dog.', NULL, NULL, '8.9', '11.0');
INSERT INTO `TalkShowTexts` VALUES (318543, 13, 1, '请来杯可乐.', 'Cola, please.', NULL, NULL, '36.7', '38.3');
INSERT INTO `TalkShowTexts` VALUES (318543, 14, 1, '有汤水吗？', 'Can I have some soup?', NULL, NULL, '38.6', '40.3');
INSERT INTO `TalkShowTexts` VALUES (318543, 22, 1, '一共是13块25分.', 'It’s thirteen dollars and twenty-five cents.', NULL, NULL, '62.0', '67.1');
INSERT INTO `TalkShowTexts` VALUES (318543, 26, 1, '好好用餐.', 'Enjoy your meal!', NULL, NULL, '72.5', '74.1');
INSERT INTO `TalkShowTexts` VALUES (318548, 3, 1, '稍后...', 'Later...', NULL, NULL, '15.2', '16.8');
INSERT INTO `TalkShowTexts` VALUES (318548, 8, 1, '还有一个小时!', 'One hour to go! ', NULL, NULL, '33.5', '35.7');
INSERT INTO `TalkShowTexts` VALUES (318548, 11, 1, ' 嘿,天上有乌云.', 'Hey, there are some dark clouds in the sky. ', NULL, NULL, '50.3', '54.9');
INSERT INTO `TalkShowTexts` VALUES (318548, 19, 1, '哦亲爱的!现在下雨了.', 'Oh dear! It’s rainning now! ', NULL, NULL, '77.0', '80.8');
INSERT INTO `TalkShowTexts` VALUES (318548, 20, 1, '那里是干的.', 'It’s dry over there. ', NULL, NULL, '81.3', '83.3');
INSERT INTO `TalkShowTexts` VALUES (318548, 26, 1, '看起来你要挨饿了,西蒙!', 'It looks like you’re going to stay hungry, Simon! ', NULL, NULL, '100.7', '104.4');
INSERT INTO `TalkShowTexts` VALUES (318549, 2, 1, '天津将是多云天气.', 'It will be cloudy in Tianjin.', NULL, NULL, '8.7', '11.3');
INSERT INTO `TalkShowTexts` VALUES (318552, 4, 1, '然后我合上书，去了动物园。', 'So I closed the book and went to the zoo.', NULL, NULL, '16.3', '20.6');
INSERT INTO `TalkShowTexts` VALUES (318553, 11, 1, '我们为了躲雨跑得很快。', 'We ran quickly to hide.', NULL, NULL, '43.5', '45.9');
INSERT INTO `TalkShowTexts` VALUES (318553, 12, 1, '我们留下了我们的食物。', 'We left our food.', NULL, NULL, '46.4', '48.6');
INSERT INTO `TalkShowTexts` VALUES (318554, 4, 1, '它在哭。', 'It’s crying.', NULL, NULL, '11.2', '12.6');
INSERT INTO `TalkShowTexts` VALUES (318556, 1, 1, '太阳在海上发光，发出好明亮的光。', 'The sun is shining on the sea, shining, shining brightly.', NULL, NULL, '6.3', '11.7');
INSERT INTO `TalkShowTexts` VALUES (318558, 3, 1, '她在为你的生日聚会买东西。', 'She’s buying things for your birthday party.', NULL, NULL, '8.2', '12.2');
INSERT INTO `TalkShowTexts` VALUES (318558, 15, 1, '快来，西蒙！我们需要你的帮助。', 'Come on, Simon! We need your help!', NULL, NULL, '56.1', '60.3');
INSERT INTO `TalkShowTexts` VALUES (318558, 16, 1, '西蒙，抓住气球！', 'Simon, grab the ballnoons!', NULL, NULL, '60.4', '63.1');
INSERT INTO `TalkShowTexts` VALUES (318558, 17, 1, '它们要飞走了！', 'They’re flying away!', NULL, NULL, '63.2', '65.1');
INSERT INTO `TalkShowTexts` VALUES (318561, 9, 1, '谁能扶我上一下楼梯呢？', 'Who help me go up the stairs?', NULL, NULL, '34.9', '38.0');
INSERT INTO `TalkShowTexts` VALUES (318562, 1, 1, '铃在响。', 'The bell is ringing.', NULL, NULL, '3.9', '5.8');
INSERT INTO `TalkShowTexts` VALUES (318562, 2, 1, '狗在吠。', 'The dog is barking.', NULL, NULL, '6.0', '7.8');
INSERT INTO `TalkShowTexts` VALUES (318563, 3, 1, '但是电话响了。', 'but the telephone rings.', NULL, NULL, '14.9', '16.9');
INSERT INTO `TalkShowTexts` VALUES (318563, 11, 1, '更多的朋友到了。', 'More friends arrive.', NULL, NULL, '46.2', '48.9');
INSERT INTO `TalkShowTexts` VALUES (318564, 4, 1, '我几乎看不见你了。', 'I can hardly see you.', NULL, NULL, '12.0', '13.7');
INSERT INTO `TalkShowTexts` VALUES (318565, 6, 1, '一个男人戴着大大的帽子，穿着雨衣。', 'A man is wearing a big hat and a raincoat.', NULL, NULL, '29.7', '34.7');
INSERT INTO `TalkShowTexts` VALUES (318565, 8, 1, '火车驶入隧道。', 'The train goes into a tunnel.', NULL, NULL, '43.2', '46.2');
INSERT INTO `TalkShowTexts` VALUES (318567, 1, 1, '欢迎回家！', 'Welcome home!', NULL, NULL, '2.9', '4.9');
INSERT INTO `TalkShowTexts` VALUES (318567, 2, 1, '你在地球上买了什么。', 'What did you bought on the earth.', NULL, NULL, '5.4', '8.6');
INSERT INTO `TalkShowTexts` VALUES (318568, 4, 1, '是一本关于宇宙航行的书。', 'It was a book about space travel.', NULL, NULL, '13.1', '16.7');
INSERT INTO `TalkShowTexts` VALUES (318568, 5, 1, '大明对宇宙航行非常感兴趣。', 'Daming is very interested in space travel.', NULL, NULL, '17.2', '22.1');
INSERT INTO `TalkShowTexts` VALUES (318568, 11, 1, '西蒙看到中国的宇宙飞船非常的惊讶。', 'Simon was surprised to see spaceships from China.', NULL, NULL, '47.9', '52.0');
INSERT INTO `TalkShowTexts` VALUES (318568, 14, 1, '他们一起做了一架纸飞船。', 'Then they made a paper spaceship together.', NULL, NULL, '65.8', '69.9');
INSERT INTO `TalkShowTexts` VALUES (318570, 1, 1, '大明和西蒙做了一个中国宇宙飞船的模型。', 'Daming and Simon made a model of a Chinese spaceship.  ', NULL, NULL, '0.0', '5.7');
INSERT INTO `TalkShowTexts` VALUES (318570, 2, 1, '宇宙飞船的名字是神舟五号。', 'The name of the spaceship is Shenzhou V.', NULL, NULL, '6.0', '10.4');
INSERT INTO `TalkShowTexts` VALUES (318570, 3, 1, '在2003年，它首次载着中国的宇航员进入太空。', 'In two thousand and three, it took a Chinese taikonaut into space for the first time.', NULL, NULL, '10.9', '18.2');
INSERT INTO `TalkShowTexts` VALUES (318570, 4, 1, '它也载着中国的国旗和一些种子。', 'It took the national flag of China and some seeds, too.', NULL, NULL, '18.5', '23.8');
INSERT INTO `TalkShowTexts` VALUES (318572, 3, 1, '很久前我坐在飞船里飞去了地球。', 'I flew to the earth in it long ago.', NULL, NULL, '14.5', '19.0');
INSERT INTO `TalkShowTexts` VALUES (318573, 5, 1, '他在太空中度过了21小时。', 'He spent about twenty-one hours in space.', NULL, NULL, '19.4', '23.6');
INSERT INTO `TalkShowTexts` VALUES (318573, 7, 1, '他也拍摄了太空录像。', 'He also made a video in space.', NULL, NULL, '27.3', '30.3');
INSERT INTO `TalkShowTexts` VALUES (318573, 14, 1, '终有一日我也想飞上太空。', 'I want to go into space someday, too.', NULL, NULL, '54.2', '58.0');
INSERT INTO `TalkShowTexts` VALUES (318575, 1, 1, '海伦凯勒1880年生于美国。', 'Helen Keller was born in the US in eighteen eighty.', NULL, NULL, '0.0', '5.0');
INSERT INTO `TalkShowTexts` VALUES (318575, 2, 1, '她在19个月时生了病。', 'She had an illness at nineteen months old.', NULL, NULL, '5.4', '8.7');
INSERT INTO `TalkShowTexts` VALUES (318575, 6, 1, '海伦环游世界讲述她的故事。', 'Helen travelled round the world and told her story.', NULL, NULL, '25.4', '29.8');
INSERT INTO `TalkShowTexts` VALUES (318575, 8, 1, '她享誉全世界。', 'She was famous all over the world.', NULL, NULL, '35.3', '38.9');
INSERT INTO `TalkShowTexts` VALUES (318578, 1, 1, '萨姆进入教室看到一些同学。', 'Sam came into the classroom and saw some classmates there.', NULL, NULL, '0.0', '4.9');
INSERT INTO `TalkShowTexts` VALUES (318578, 2, 1, '他们头上有茶杯。', 'They had cups on their heads.', NULL, NULL, '5.1', '8.4');
INSERT INTO `TalkShowTexts` VALUES (318578, 6, 1, '他们之前打算去进行棒球比赛。', 'They planned to play a baseball game.', NULL, NULL, '22.1', '25.3');
INSERT INTO `TalkShowTexts` VALUES (318578, 8, 1, '但是玲玲带了一些茶杯。', 'But Lingling brought some cups.', NULL, NULL, '33.5', '37.2');
INSERT INTO `TalkShowTexts` VALUES (318578, 10, 1, '然后他们把茶杯放到他们头上后笑的更凶，萨姆笑着说了。', 'Then they put the cups on their heads and laughed more.  Sam smiled. ', NULL, NULL, '41.3', '49.2');
INSERT INTO `TalkShowTexts` VALUES (318578, 11, 1, '英语单词很容易说错。', 'It’s easy to make mistake with English words, he said.', NULL, NULL, '51.0', '57.0');
INSERT INTO `TalkShowTexts` VALUES (318582, 1, 1, '祝你一切都好！', 'Best wishes to you！', NULL, NULL, '4.5', '6.5');
INSERT INTO `TalkShowTexts` VALUES (318582, 2, 1, '祝你好运！', 'Good luck to you!', NULL, NULL, '6.7', '8.4');
INSERT INTO `TalkShowTexts` VALUES (318583, 3, 1, '我们就要和我们的小学说再见了。', 'We’re going to say goodbye to our primary school soon.', NULL, NULL, '7.3', '12.0');
INSERT INTO `TalkShowTexts` VALUES (318583, 7, 1, '我会永久保存它的。', 'I’ll keep it forever.', NULL, NULL, '23.2', '25.5');
INSERT INTO `TalkShowTexts` VALUES (318583, 14, 1, '你带给我很多欢乐。', 'You brought us lots of joy.', NULL, NULL, '59.3', '62.3');
INSERT INTO `TalkShowTexts` VALUES (318583, 15, 1, '祝你前程似锦！', 'Good luck for the future!', NULL, NULL, '62.6', '64.7');
INSERT INTO `TalkShowTexts` VALUES (318583, 22, 1, '你是个很棒的朋友。', 'You’re a wonderful friend.', NULL, NULL, '84.1', '86.9');
INSERT INTO `TalkShowTexts` VALUES (318586, 7, 1, '希望你开心每一天。', 'Wishing you happiness every day.', NULL, NULL, '37.9', '42.2');
INSERT INTO `TalkShowTexts` VALUES (318587, 1, 1, '我要去湖泊中学。', 'I’m going to Lake Middle School.', NULL, NULL, '0.3', '3.5');
INSERT INTO `TalkShowTexts` VALUES (318588, 1, 1, '玲玲的告别演说', 'A goodbye speech by Lingling', NULL, NULL, '0.0', '3.4');
INSERT INTO `TalkShowTexts` VALUES (318588, 4, 1, '在这个九月开始中学生活了。', 'and start middle school this September.', NULL, NULL, '10.1', '13.4');
INSERT INTO `TalkShowTexts` VALUES (318588, 5, 1, '我很激动，也很悲伤。', 'I’m excited, and also sad.', NULL, NULL, '14.0', '18.0');
INSERT INTO `TalkShowTexts` VALUES (318588, 6, 1, '激动是因为我们要学习中文，英语，数学，历史和地理。', 'I’m excited because we’re going to study Chinese, English, Maths, History, Geography,', NULL, NULL, '19.2', '29.1');
INSERT INTO `TalkShowTexts` VALUES (318588, 9, 1, '与此同时，要和你们说再见我感到非常伤心。', 'At the same time, I’m very sad to say goodbye to you.', NULL, NULL, '37.6', '43.4');
INSERT INTO `TalkShowTexts` VALUES (318588, 14, 1, '六年前，他们只会说一点中文。', 'Six years ago, they spoke only very little Chinese.', NULL, NULL, '59.2', '64.8');
INSERT INTO `TalkShowTexts` VALUES (318588, 16, 1, '在英国也要继续练习中文。', 'Keep on practising Chinese in the UK.', NULL, NULL, '69.0', '72.8');
INSERT INTO `TalkShowTexts` VALUES (318588, 17, 1, '时不时回中国看看！', 'And come back to China sometime!', NULL, NULL, '73.0', '76.0');
INSERT INTO `TalkShowTexts` VALUES (318588, 19, 1, '让我们给彼此写很多邮件吧！', 'Let’s write lots of emails to each other!', NULL, NULL, '80.4', '83.9');
INSERT INTO `TalkShowTexts` VALUES (318590, 8, 1, '我要学物理，化学和中文。', 'I’m going to study Physics, Chemistry and Chinese.', NULL, NULL, '29.3', '36.0');
INSERT INTO `TalkShowTexts` VALUES (319085, 5, 1, '肯将会弹吉他，大卫将会敲鼓。', 'Ken will play the guitar and David will play the drums.', NULL, NULL, '18.3', '24.7');
INSERT INTO `TalkShowTexts` VALUES (320017, 5, 1, '我喜欢薯条。', 'I like French fries.', NULL, NULL, '21.5', '24.0');
INSERT INTO `TalkShowTexts` VALUES (320044, 5, 1, '哦，这是一个卷笔刀。', 'Oh, it’s a pencil sharpener.', NULL, NULL, '14.8', '18.5');
